	[47033] = "{id = 47033, NameZh = '大小姐的洋伞', Icon = 'item_47033', Type = 810, Quality = 3, MaxNum = 1, SellPrice = 100, AdventureSort = 9502, AdventureValue = 1, AdventureReward = _EmptyTable, StorageReward = {buffid = {80012250}}, Condition = 8, Desc = '大陆最著名的香水世家：穆拉辛家族的第十四代目社长维特尔·穆拉辛的独生女儿：马蒂尔·穆拉辛小姐从小就在众星捧月般的环境中长大。为了抵抗盛夏的阳光直射，皮肤白皙的她令人制作了这把精致的魔导阳伞，据传言，打着这把伞的时候可以有效抵抗日照的影响，并且，仿佛可以将周围的空气温度恒久保持在24度。', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
	[47034] = "{id = 47034, NameZh = '月之兔兔', Icon = 'item_47034', Type = 810, Quality = 1, MaxNum = 1, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[47035] = "{id = 47035, NameZh = '蔚蓝的依斯鲁得海', Icon = 'item_47035', Type = 810, Quality = 1, MaxNum = 1, SellPrice = 100, AdventureSort = 9508, AdventureValue = 1, AdventureReward = _EmptyTable, StorageReward = {buffid = {80012300}}, Condition = 8, Desc = '航海日记的最后写到：在月亮第51次沉入宛如天鹅绒的大海，我们终于看到了岸边的灯火，康韦斯特和摩尔仑的眼眶里都挂着激动的泪水，这二个傻大个儿哭泣，除了在父亲的葬礼上，这还是我人生中的第二次看到。我们沉默但默契地开始拿出手摇浆，哼着《天之津的老人》，我们出生后就学会的歌曲，然而现在，我们驶向岸边，那个父亲和母亲的故乡。', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
	[47036] = "{id = 47036, NameZh = '万圣惊魂夜', Icon = 'item_47036', Type = 810, Quality = 1, MaxNum = 1, SellPrice = 100, AdventureSort = 9514, AdventureValue = 1, AdventureReward = _EmptyTable, StorageReward = {buffid = {80012420}}, Condition = 8, Desc = '老旧的银质镶边书本静静躺在伯爵城堡的废弃礼拜堂地上，支离破碎的彩绘玻璃外是苍冷的月光，透过那些仿佛凝结在时光中的空气尘埃，照射到已经没有了长指针的巨大时记上。突然“咔哒”一声，短指针跨到了12（0）这个数字...', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
	[47037] = "{id = 47037, NameZh = '温蒂', Icon = 'item_47037', Type = 810, Quality = 1, MaxNum = 1, AuctionPrice = 1, SellPrice = 100, AdventureSort = 10021, AdventureValue = 90, AdventureReward = _EmptyTable, StorageReward = {buffid = {80012370}}, Condition = 8, Desc = '温蒂明白，它必须始终注视着艾希。艾希明白，温蒂始终会注视着她。哪怕温蒂的背后不只是一双眼睛，但是那其中只要有TA在，她便心存温暖。', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
	[47038] = "{id = 47038, NameZh = '宝贝·别哭', Icon = 'item_47038', Type = 810, Quality = 3, MaxNum = 1, SellPrice = 100, AdventureSort = 9520, AdventureValue = 1, AdventureReward = _EmptyTable, StorageReward = {buffid = {80012490}}, Condition = 8, Desc = '贝蒂明白，她一直在等的那个少女也许再也不会回来了，在那个敲响钟声的飘雪之夜之后，少女的目光不再只停留在贝蒂身上，少女的时间不再停留在那一刻。少女长大了，被一双和她父亲一样健壮有力的手牵着，走向灯火霓虹的圣诞夜集市。贝蒂依然会在这里播散这里名为祝福的礼物。', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
