	[48019] = "{id = 48019, NameZh = '幸福的小鸟', Icon = 'item_48019', Type = 840, Quality = 3, MaxNum = 1, SellPrice = 100, AdventureSort = 9532, AdventureValue = 1, AdventureReward = _EmptyTable, StorageReward = {buffid = {80012500}}, Condition = 8, Desc = '孩子们依然在银白色的雪地里嬉戏，不远处的教堂钟声清澈地传来，埃尔文轻轻拉起了丽安娜的小手，被冻得红灿灿的小手，放在嘴边呼出白色的云彩，“暖和一点了吗？”“嗯~只要在你身边，我一直很温暖呢~”，两人的身影渐渐消失在安静飘落的初春之雪中，屋檐上两只金色的小鸟唱着只有幸福的人们才能听到的歌谣~', ItemTarget = _EmptyTable, Share = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
	[48020] = "{id = 48020, NameZh = '名伞·和之美', Icon = 'item_48020', Type = 840, Quality = 3, MaxNum = 1, SellPrice = 100, AdventureSort = 9539, AdventureValue = 1, AdventureReward = _EmptyTable, StorageReward = {buffid = {80012870}}, Condition = 8, Desc = '天水之国的象征：天水之国·天津町的特产油纸伞，花纹华贵优美，在普隆德拉最近人气很高。', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
	[48021] = "{id = 48021, NameZh = '校园生活', Icon = 'item_48021', Type = 840, Quality = 3, MaxNum = 1, SellPrice = 100, AdventureSort = 9545, AdventureValue = 1, AdventureReward = _EmptyTable, StorageReward = {buffid = {80012810}}, Condition = 8, Desc = '智美酱今天的日记一如既往地流水账，正符合了他这个无忧无虑的纯真年龄，虽然例如早上睡过头，吊着面包片在母亲的责骂声中，可爱制服衬衫的一角从百褶裙种逃出来就在坂道上奔跑。例如，原本天真以为用书本架在面前就可以挡住老师的视线，但是却败在自己的白日春梦，叫出学长名字而成为全班一天的笑料~嘛~真是棒呢~这就是青春呀~（笑）', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
	[48022] = "{id = 48022, NameZh = '至高王冠', Icon = 'item_48022', Type = 800, Quality = 4, MaxNum = 1, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = {buffid = {80012900}}, Desc = '纯金打造的至高王之冠，内刻神文“献于人王”，由奥丁赠送给人类历史里超越国家与阶级的英雄领袖。上一位人选是魔剑士达纳托斯，他手持无名之剑，沐浴在黎明的辉光中，为了信任自己的所有人类，与魔王梦罗克决一死战，以超越常人的力量与博大宽广的胸怀向世人展现何为伟大。', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
	[48023] = "{id = 48023, NameZh = '星光白冠', Icon = 'item_48023', Type = 800, Quality = 4, MaxNum = 1, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = {buffid = {80012900}}, Desc = '摘取夜空里的冷星，云雾之神茉里美糅合白金与星光，为自己待嫁的女儿打造了无暇的星光白冠。佩戴白冠的云雾仙女远嫁精灵王，在末日来临之际带领精灵一族重获新生，她的壮举被精灵们铭记，而她也被精灵们尊称为星之神女。', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
	[48024] = "{id = 48024, NameZh = '亡灵黑冠', Icon = 'item_48024', Type = 800, Quality = 4, MaxNum = 1, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = {buffid = {80012900}}, Desc = '异星互相碰撞飞溅出的陨铁，经过无数摩擦从天空中落下，被好奇的矮人打造成一顶黑金王冠。相传拥有这个王冠的人能获得号令亡灵的力量，同时也会因为佩戴黑冠渐渐失去记忆。千年之前，圣战中一名英勇的牧师眼见人类将面临灭绝的危险，毅然戴上这顶黑冠，号令圣战中牺牲的战士灵魂，作为黑暗里的英雄带领人类走向光明。无名的牧师失去了记忆，也失去与主神的荣光，忘记一切，整日徘徊在亡灵之中，渐渐被重获繁荣的人类所遗忘，仅在吟游诗人的只言片语中能听见有关他与亡灵黑冠的事迹。', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
