	[48605] = "{id = 48605, NameZh = '玫瑰花纱巾', Icon = 'item_48605', Type = 830, Quality = 2, MaxNum = 1, SellPrice = 100, AdventureSort = 9536, AdventureValue = 1, AdventureReward = _EmptyTable, StorageReward = {buffid = {80012660}}, Condition = 8, Desc = '蔷薇隐藏起我的爱慕，这个还未开春的季节，脸上温热的红晕，正是以为你。焦虑踌躇着，盼望着那一天的到来。等着你的那句话。为了这个情景，我每天都在努力练习SAY YES。', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
	[48606] = "{id = 48606, NameZh = '般若之面', Icon = 'item_48606', Type = 830, Quality = 2, MaxNum = 1, SellPrice = 100, AdventureSort = 762, AdventureValue = 160, AdventureReward = {buffid = {61000450}}, StorageReward = {buffid = {80012880}}, Condition = 4, ComposeID = 17606, Desc = '因女人强烈的怨恨而形成的恶鬼般若，是普通鬼怪都害怕的恶灵。天水之国的巫女们将般若的面孔画在白色的面具之上，制作成般若之面，送给需要夜行的游人，保护他们不受野鬼的骚扰和袭击。', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
