	[53014] = "{id = 53014, NameZh = '少女的发簪', Icon = 'item_45190', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【古城末日“叛变者”调查档案1号】卡珊德拉公主的饰物，上面刻有她的名字缩写，是当时流行的款式。由欧律皮罗斯男爵赠送给公主作为定情信物。发簪有烧伤过的痕迹，传说公主曾经因一场大火而毁容，但男爵仍然承诺将娶她为妻。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53015] = "{id = 53015, NameZh = '损毁的头盔', Icon = 'item_task_29', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【古城末日“叛变者”调查档案2号】俄思鲁将军的头盔，其上有一道深而长的刀痕，那是和某位男爵决斗时留下的耻辱。后来，这位将军因骁勇善战不苟言笑，被卡珊德拉公主赐予“寒冰锻造的利刃”之称，并拔剑在头盔上划下了另一道痕迹。不知公主是不是在开玩笑，不过这个头盔伴随着将军一生。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53016] = "{id = 53016, NameZh = '古旧的勋章', Icon = '27', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【古城末日“叛变者”调查档案3号】这是施密特王朝特制的战士勋章，徽纹代表着持有者为施密特家族的亲信，骑士团中只有极少数人会被国王授予这种勋章。它并非代表荣耀，而是成为施密特王家的影子，执行秘密的任务。在这枚勋章上，竟然刻着卢恩王国七大家族之一，鲁贝布勒格家族的姓氏。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53017] = "{id = 53017, NameZh = '深红的披风', Icon = 'item_43002', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【古城末日“叛变者”调查档案4号】披风里衬上有伊多墨将军的名字，这位赫赫有名的施密特王朝大将，与俄思鲁将军并称“王国双璧”，生前两人亦是挚友。不过关于这位将军的死，传言却是扑朔迷离。据说将军并非战死，而是被暗杀。“俄思鲁将军秘密处死男爵家仆”的王家丑闻事件，被认为与伊多墨将军之死有关。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53018] = "{id = 53018, NameZh = '破碎的宝石', Icon = 'item_5102', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【古城末日“叛变者”调查档案5号】这颗宝石曾被镶嵌在施密特国王的权杖上，世代相传。宝石的碎裂，映照着这个王族的末路。大贤者巴特蒙德曾警告过国王，将这颗宝石从权杖上取下并丢弃，否则王国必将迎来末日。于是，这颗宝石就被锁进了宝箱，国王称它为“恶魔的诱惑”。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53019] = "{id = 53019, NameZh = '残旧的匕首', Icon = 'item_40724', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【古城末日“叛变者”调查档案6号】“阿芙洛忒的匕首”是它久违流传之名，刀柄上的纹章属于克雷霍尔伦家族，因此它被称为“弑君者匕首”。篡位者吉奥布罗克国王的王后阿芙洛忒命一位手艺精湛的工匠锻造了它，并赠送给丈夫。这把匕首后来一直被克雷霍尔伦家族保存，最后一位主人将它锁进了箱子里，如同想要将克雷霍尔伦家族那不能明言的过去藏起，渴望那沉重的负担从此被人遗忘。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53020] = "{id = 53020, NameZh = '祈祷的项链', Icon = 'item_44019', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【古城末日“叛变者”调查档案7号】安德斯神父的十字架项链，过去，他常常握着它祈祷施密特王族的永久康盛，并祝福他的爱人幸福。谣传他过去是皇家宫廷乐师，因被某位伯爵诬陷暗中爱慕王后，为了王后的名誉而选择离开王庭，隐居在修道院中，除了祷告之外，又领养了许多贫苦孤儿。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53021] = "{id = 53021, NameZh = '罗娜的魔书', Icon = 'item_42518', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【古城末日“叛变者”调查档案8号】罗娜的魔法书，记录着她抄录的那些失传的古魔法咒语。罗娜被认为是邪恶的巫师，她孤独地居住在一间偏僻小屋里，研究着在人们眼中被视为最邪恶禁忌的魔法，因此没有人愿意接近她的住处，只有安德斯神父愿意和她交谈。她送了神父一条项链，说是能带来幸福。这本书在她死后，被转交给安德斯神父保管。不久之后，安德斯神父又对一位来访者宣称，遗失了这本书……', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53022] = "{id = 53022, NameZh = '士兵的日记', Icon = 'item_3002', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【古城末日“叛变者”调查档案9号】古城守备士兵的日记，里面写满了枯燥的巡逻日常、成年人的深夜笑话、下水道的鬼怪传说、施密特王族的那些王家闹剧、流浪狗“小柯”的饲养日志、对修道院里传出的琴声品评……最后一篇日记停留在两百年前的那个夜晚，上面写道：恶魔来了，但海因里希将军会带领我们战胜恶魔。等我凯旋归来，我决定对那条流浪犬好一点……', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53023] = "{id = 53023, NameZh = '腐蚀的头骨', Icon = 'item_task_30', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【古城末日“叛变者”调查档案10号】据传是施密特王朝第一任国王的头骨，又相传是国王祖父的头骨，也有流传说是先代国王的战友大魔导士路西菲尔的头骨……总之，这颗神秘的头骨被封存在王族后花园地窖中很多年。触碰头骨，似乎能看到头骨上飘出一个白糊糊的鬼影……', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53024] = "{id = 53024, NameZh = '残缺的石碑', Icon = 'item_5027', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【古城末日“叛变者”调查档案11号】卡珊德拉公主的墓碑，这位公主死于一场传染病，临终前十分孤独凄惨，只有一名忠心的侍女照顾着她。石碑上写着她的墓志铭：愿宽容如烈火带来温暖，愿善良之人终得一切恩露之报。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53025] = "{id = 53025, NameZh = '骑士的羽穗', Icon = 'item_45089', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【古城末日“叛变者”调查档案12号】欧律皮罗斯男爵家族特有的象征，它被拔下来，伴随着卡珊德拉公主度过了病患余生。男爵在破城之日，于公主墓碑前刎颈自杀，当时他手中握着这根羽穗，在墓碑前的地上用剑刻下了愧疚之言。男爵倒在血泊中时，羽穗随风飘远，爱意随风而逝。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53026] = "{id = 53026, NameZh = '宰相的怀表', Icon = 'item_task_31', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【古城末日“叛变者”调查档案13号】奥德修斯宰相的怀表，是他非常珍爱之物。据说是妻子的遗物，宰相日理万机时总是打开它确认时间，从未让它离开过身边。但它现在却没有陪伴着宰相的遗体，是有人将它偷走了吗？怀表本身价值不菲，曾经有人向宰相高价收取这块怀表，当然被拒绝了。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53027] = "{id = 53027, NameZh = '贵妇的礼帽', Icon = 'item_task_32', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【古城末日“叛变者”调查档案14号】海因里希执政期间，曾因为这顶礼帽而闹出过一段绯闻，它原先被摆放在海因里希的书房中，后来却出现在了一位贵妇的头上。不过严肃古板的执政官阁下，可不像是会偷偷赠送贵妇礼帽的那种男人啊，大家都认为他这辈子唯一的情人就是他的工作。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53028] = "{id = 53028, NameZh = '国王的宝剑', Icon = 'item_40332', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【古城末日“叛变者”调查档案15号】初代施密特国王使用过的剑，此后，每一任国王都会在加冕仪式上接过它。它见证了施密特王族的兴衰。最后一任国王将它留在了克雷斯特汉姆古城，远赴他乡再也没有回来。海因里希执政官总是遥遥望着这把国王宝剑叹息。希默梅思用它斩下了海因里希的头颅，然后随手把它丢弃了，于是它所承载的一代王朝落下帷幕。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53029] = "{id = 53029, NameZh = 'GLC.A031号档案', Icon = 'task_certificate', Type = 160, Quality = 4, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = 'A.W800年，希默梅思带着不死军团攻入克雷斯特汉姆，皇家军团缚手难敌，王城一夜之间变为死城。有人认为，皇家中有“叛变者”向希默梅思泄露军情。一位秘密调查员根据仅存下来的皇家文献，锁定了十五个嫌疑人，并将这十五个人的资料编集为【古城末日“叛变者”档案】。档案中关于“叛变者”的真实身份并未明确提及，但从那些相关物品的信息中可寻得蛛丝马迹。档案详情在春楠那里可以复查。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53030] = "{id = 53030, NameZh = '破旧王冠', Icon = 'item_task_28', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '造型夸张的王冠，在岁月的侵蚀下变得破损不堪，上面隐约雕刻着三个模糊的字…', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53031] = "{id = 53031, NameZh = '碧蓝宝石', Icon = 'item_task_27', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '碧蓝色的宝石，通体晶莹剔透，完全没有深埋地下的泥土气息…', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53032] = "{id = 53032, NameZh = '赤红晶钻', Icon = 'item_task_26', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '赤红色的钻石，时不时地散发出妖异的红光，内部有着不知名的力量涌动…', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53033] = "{id = 53033, NameZh = '休密兹的王冠', Icon = 'item_task_25', Type = 160, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '暴君休密兹在曾经被巨人族与诸神大战中毁灭的遗址上建立了克雷斯特汉姆。休密兹的威望越来越高最后掌握了无所不为的大权。随著王宫改建的开始，他花税金如流水，他那些善于阿谀奉承的心腹向他进言，为了彰显他的无上王权，休密兹需要一个世界上独一无二的王冠。很快，休密兹就搜集到了两颗珍惜无比的宝石，命令宝石工匠，日夜雕琢，又运来最珍贵的金属来打造王冠的底座。甚至有数名宝石工匠因为持续工作而暴毙，这才在加冕之时把王冠制作出来。这个王冠也被民众暗地里称之为“暴君之冠”。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53034] = "{id = 53034, NameZh = '虎蜥酒', Icon = 'item_task_10', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '布兰奇亲手制作的美酒。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53035] = "{id = 53035, NameZh = '毒药', Icon = 'item_5021', Type = 160, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【楔子】许多故事的开始，都是伴随着阴谋、凶杀、冤情、争斗。那天晚上，莫里茨公爵冰冷地躺在床上，脚边倒着一瓶毒药。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53036] = "{id = 53036, NameZh = '魔眼', Icon = 'item_7337', Type = 160, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【憎恶之书·公正】“是献出你的灵魂，还是让我夺走你朋友的生命，做出选择吧！”恶魔用它那似乎能洞察人类内心的眼睛，微笑着流露出狰狞的目光，对面前的驱魔人说道。卡伦赞德正是被这双魔眼所蛊惑。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53037] = "{id = 53037, NameZh = '符文', Icon = 'item_12004', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【憎恶之书·真相】驱魔人的符上，狂乱的字迹流露出书写者内心的挣扎与不安，是用力量维护公正，还是用力量解救落难的生命？驱魔人明亮的眼睛能看透黑暗，却看不透世间的正义。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53038] = "{id = 53038, NameZh = '钥匙', Icon = 'item_40011', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【恐惧之书·孤独】阴冷的监狱中，往往隐藏着禁忌、罪恶的故事。而用来开启它们的钥匙，就像是巫婆的苹果。至情至义的驱魔人知道这样做违背了法律与秩序，但他还是用钥匙打开了牢门，放走了他最好的朋友。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53039] = "{id = 53039, NameZh = '手铐', Icon = 'item_5042', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【恐惧之书·真相】牢房狭小的窗洞漏进清冷的月光，当骄傲的驱魔人失去自由之时，仿佛也失去了所有，包括自己存在的价值。链铐的响动在提醒着他犯下的错误。他无法向任何人倾诉苦衷，因为是他自己将自己推进了孤独的深渊。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53040] = "{id = 53040, NameZh = '粮食', Icon = 'item_12305', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【怯懦之书·仁慈】放置在我们眼前的普通食物，困苦的时候，它会比金子更珍贵渴求，而富裕之时，它却廉价得仿佛脚边的野草，随意可践踏忽视。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53041] = "{id = 53041, NameZh = '金币', Icon = 'item_105', Type = 160, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【怯懦之书·真相】金钱就像是这世间的一切诱惑，过度纵容，就会导致极端的后果。为了满足逐渐贪得无厌的卡伦赞德的要求，驱魔人放弃了一切，而这并没有带来善果。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53042] = "{id = 53042, NameZh = '书卷', Icon = 'item_42519', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【嫉妒之书·睿智】古列弗深爱书籍，他相信那是它最忠诚而值得信赖的朋友。因此，他有一颗博大精深的头脑，总是能想出绝妙的主意扭转乾坤。但这并没有给他带来好下场。智者千虑却必有一失。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53043] = "{id = 53043, NameZh = '冕冠', Icon = 'item_45046', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【嫉妒之书·真相】卡伦赞德的野心愈加庞大，最后，他看向了那顶璀璨的冕冠。华丽的外表下，早已是枯朽的灵魂。他泯灭了一切良善，冷酷无情得就像一个真正的恶魔。欲望的终点，往往是穷途末路。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53044] = "{id = 53044, NameZh = '利刃', Icon = 'item_40305', Type = 160, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【尾声】驱魔人行走在夜间，以狩猎魔鬼为职业的他，今晚要去狩猎一个真正的恶魔。他握着锋利的武器，内心默念：“卡伦赞德，你曾是我最重要的朋友，所以也必须由我来洗净你的罪孽。而我将背负罪孽，永生潜行在黑夜中。”', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53045] = "{id = 53045, NameZh = 'GLC.X114号档案', Icon = 'task_certificate', Type = 160, Quality = 4, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '记录了四个互为挚友并深受国王恩宠的驱魔人互相间打了一个赌，然而谁也没有真正在意这场赌局。但命运的嘲讽却悄然降临在他们身上，为了挽救他们的好友卡伦赞德，四人使用了不同的方法，却酿成了一场惨痛悲剧。档案详情在影子驱魔人那里可以复查。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53046] = "{id = 53046, NameZh = '初见的华尔兹', Icon = 'item_48545', Type = 160, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【第一乐章】假面舞会上的面具。它记录着桂瑟兰妮与加布里尔在假面之下开始的一段美丽华尔兹。初见之时，未知的一切，华丽的盛装，彼此的吸引，在富丽堂皇的舞池中相映生辉，童话一般的美好而浪漫。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53047] = "{id = 53047, NameZh = '玫瑰的踢踏舞', Icon = 'item_task_16', Type = 160, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【第二乐章】凋零的荆棘玫瑰。它被收藏在加布里尔的书桌右手边第一格抽屉中，制成了精巧的干花，夹在一本名为《梦中婚礼》的书中。当死讯传来之时，桂瑟兰妮亲手将它放进了加布里尔的遗棺中。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53048] = "{id = 53048, NameZh = '爱情的圆舞曲', Icon = 'item_40331', Type = 160, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【第三乐章】细长锋利的西洋剑。桂瑟兰妮收到这件礼物时，不知该夸赞将军心意别致还是叹息他不懂情调。很长一段时间，它都被挂在墙上当一件精致的装饰品，直到桂瑟兰妮把它拿下来，放到长眠的将军身边，并说：“谢谢你的守护。”', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53049] = "{id = 53049, NameZh = '月光的小夜调', Icon = '22', Type = 160, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【第四乐章】远方而来的战讯。“血色胜利”之战，王国军于险恶重重中获得艰难的胜利，战讯传至王城，桂瑟兰妮在侯爵府邸落下悲伤的眼泪，懊悔凝结在无声泪水中。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53050] = "{id = 53050, NameZh = '教堂的圣福音', Icon = 'item_52304', Type = 160, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【第五乐章】绅士的手帕。修道院中，忏悔的言语有谁在聆听？抑郁的容颜是为谁思念？绅士迈着轻轻的脚步靠近正在祈祷的女子身边，递出手帕款款温柔说：“请不要因为后悔而哭泣，它不适合你。我亲爱的桂瑟兰妮。”', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53051] = "{id = 53051, NameZh = '王子的嘲笑声', Icon = 'item_52316', Type = 160, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【第六乐章】碎裂的镜子。王子在富丽堂皇的舞池中央丢下假面，戏谑嘲笑道：“来不及了，你这个贪心势利的女人。破镜永不能恢复原貌。”璀璨迷人的水晶灯光下，童话似乎就在眼前破碎，再不会有浪漫的华尔兹。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53052] = "{id = 53052, NameZh = '梦中的婚礼章', Icon = 'item_45198', Type = 160, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '【第七乐章】枯黄的落叶。“如果，我不能在神父的面前吻你，幸福永不会降临在你我身上。”表达着如此爱意的人，在希默梅思的降临中，永远沉浸入梦中的婚礼。落叶带走生命的韵律。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
