	[53079] = "{id = 53079, NameZh = '利维坦的沙漏', Icon = 'item_task_49', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '利维坦是古今著名的大海盗，他利用一个别致的金沙漏来打开时空之门。轻松的穿梭回过去，解决改变未来将出现的麻烦。一天，利维坦的船在海上遇到风暴，嫉妒他的副手乘乱砸破了他的金沙漏，利维坦瞬间化作一缕金沙与沙漏中的沙随风暴飘散在无边无际的大海之中。后来陷害他的副手死于非命，但也没人再见过位大名鼎鼎的海盗。钟楼2F的钟表迷库马斯坦正在寻找这个物品。', ItemTarget = _EmptyTable, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53080] = "{id = 53080, NameZh = '明日水晶', Icon = 'item_task_46', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '艾尔帕兰城内曾有位通晓未来的魔女，通常是神秘地地罩在面网里，不示人以真面目，脸面向的方向 ，手里永远拿一颗用丝巾包住的水晶球，但是都不展开，仿佛喻世着未来不可知的神秘，没有人知道她叫什么，问她从哪里来，她便答“遥远的未来”。钟楼2F的钟表迷库马斯坦正在寻找这个物品。', ItemTarget = _EmptyTable, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
