	[53109] = "{id = 53109, NameZh = '陈旧的告示·碎片', Icon = 'item_5128', Type = 160, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '从布袋熊的布袋中滑出一封陈旧的告示，上面登记着十年前的一天，圣诞村的宝石商人贝尔摩被洗劫的新闻。贝尔摩身中数箭身亡，当时的抢匪一共有五个人，查理、曼逊、丹、里克和卡尔。因四人指认是腹语师查理杀了贝尔摩，所以腹语师查理被判绞刑，在平安夜的前一天被处决。玩具工厂2F的腹语师梅丽莎正在寻找[c][ff7f00]10[-][/c]个这种碎片。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
