	[53500] = "{id = 53500, NameZh = '少女的发簪', Icon = 'item_45190', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23101, AdventureValue = 85, AdventureReward = {rewardid = {870001}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【古城末日“叛变者”调查档案1号】卡珊德拉公主的饰物，上面刻有她的名字缩写，是当时流行的款式。由欧律皮罗斯男爵赠送给公主作为定情信物。发簪有烧伤过的痕迹，传说公主曾经因一场大火而毁容，但男爵仍然承诺将娶她为妻。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53501] = "{id = 53501, NameZh = '损毁的头盔', Icon = 'item_task_29', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23102, AdventureValue = 85, AdventureReward = {rewardid = {870002}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【古城末日“叛变者”调查档案2号】俄思鲁将军的头盔，其上有一道深而长的刀痕，那是和某位男爵决斗时留下的耻辱。后来，这位将军因骁勇善战不苟言笑，被卡珊德拉公主赐予“寒冰锻造的利刃”之称，并拔剑在头盔上划下了另一道痕迹。不知公主是不是在开玩笑，不过这个头盔伴随着将军一生。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53502] = "{id = 53502, NameZh = '古旧的勋章', Icon = '27', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23103, AdventureValue = 85, AdventureReward = {rewardid = {870003}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【古城末日“叛变者”调查档案3号】这是施密特王朝特制的战士勋章，徽纹代表着持有者为施密特家族的亲信，骑士团中只有极少数人会被国王授予这种勋章。它并非代表荣耀，而是成为施密特王家的影子，执行秘密的任务。在这枚勋章上，竟然刻着卢恩王国七大家族之一，鲁贝布勒格家族的姓氏。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53503] = "{id = 53503, NameZh = '深红的披风', Icon = 'item_43002', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23104, AdventureValue = 85, AdventureReward = {rewardid = {870004}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【古城末日“叛变者”调查档案4号】披风里衬上有伊多墨将军的名字，这位赫赫有名的施密特王朝大将，与俄思鲁将军并称“王国双璧”，生前两人亦是挚友。不过关于这位将军的死，传言却是扑朔迷离。据说将军并非战死，而是被暗杀。“俄思鲁将军秘密处死男爵家仆”的王家丑闻事件，被认为与伊多墨将军之死有关。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53504] = "{id = 53504, NameZh = '破碎的宝石', Icon = 'item_5102', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23105, AdventureValue = 85, AdventureReward = {rewardid = {870005}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【古城末日“叛变者”调查档案5号】这颗宝石曾被镶嵌在施密特国王的权杖上，世代相传。宝石的碎裂，映照着这个王族的末路。大贤者巴特蒙德曾警告过国王，将这颗宝石从权杖上取下并丢弃，否则王国必将迎来末日。于是，这颗宝石就被锁进了宝箱，国王称它为“恶魔的诱惑”。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53505] = "{id = 53505, NameZh = '残旧的匕首', Icon = 'item_40724', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23106, AdventureValue = 85, AdventureReward = {rewardid = {870006}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【古城末日“叛变者”调查档案6号】“阿芙洛忒的匕首”是它久违流传之名，刀柄上的纹章属于克雷霍尔伦家族，因此它被称为“弑君者匕首”。篡位者吉奥布罗克国王的王后阿芙洛忒命一位手艺精湛的工匠锻造了它，并赠送给丈夫。这把匕首后来一直被克雷霍尔伦家族保存，最后一位主人将它锁进了箱子里，如同想要将克雷霍尔伦家族那不能明言的过去藏起，渴望那沉重的负担从此被人遗忘。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53506] = "{id = 53506, NameZh = '祈祷的项链', Icon = 'item_44019', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23107, AdventureValue = 85, AdventureReward = {rewardid = {870007}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【古城末日“叛变者”调查档案7号】安德斯神父的十字架项链，过去，他常常握着它祈祷施密特王族的永久康盛，并祝福他的爱人幸福。谣传他过去是皇家宫廷乐师，因被某位伯爵诬陷暗中爱慕王后，为了王后的名誉而选择离开王庭，隐居在修道院中，除了祷告之外，又领养了许多贫苦孤儿。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53507] = "{id = 53507, NameZh = '罗娜的魔书', Icon = 'item_42518', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23108, AdventureValue = 85, AdventureReward = {rewardid = {870008}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【古城末日“叛变者”调查档案8号】罗娜的魔法书，记录着她抄录的那些失传的古魔法咒语。罗娜被认为是邪恶的巫师，她孤独地居住在一间偏僻小屋里，研究着在人们眼中被视为最邪恶禁忌的魔法，因此没有人愿意接近她的住处，只有安德斯神父愿意和她交谈。她送了神父一条项链，说是能带来幸福。这本书在她死后，被转交给安德斯神父保管。不久之后，安德斯神父又对一位来访者宣称，遗失了这本书……', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53508] = "{id = 53508, NameZh = '士兵的日记', Icon = 'item_3002', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23109, AdventureValue = 85, AdventureReward = {rewardid = {870009}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【古城末日“叛变者”调查档案9号】古城守备士兵的日记，里面写满了枯燥的巡逻日常、成年人的深夜笑话、下水道的鬼怪传说、施密特王族的那些王家闹剧、流浪狗“小柯”的饲养日志、对修道院里传出的琴声品评……最后一篇日记停留在两百年前的那个夜晚，上面写道：恶魔来了，但海因里希将军会带领我们战胜恶魔。等我凯旋归来，我决定对那条流浪犬好一点……', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53509] = "{id = 53509, NameZh = '腐蚀的头骨', Icon = 'item_task_30', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23110, AdventureValue = 85, AdventureReward = {rewardid = {870010}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【古城末日“叛变者”调查档案10号】据传是施密特王朝第一任国王的头骨，又相传是国王祖父的头骨，也有流传说是先代国王的战友大魔导士路西菲尔的头骨……总之，这颗神秘的头骨被封存在王族后花园地窖中很多年。触碰头骨，似乎能看到头骨上飘出一个白糊糊的鬼影……', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53510] = "{id = 53510, NameZh = '残缺的石碑', Icon = 'item_5027', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23111, AdventureValue = 85, AdventureReward = {rewardid = {870011}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【古城末日“叛变者”调查档案11号】卡珊德拉公主的墓碑，这位公主死于一场传染病，临终前十分孤独凄惨，只有一名忠心的侍女照顾着她。石碑上写着她的墓志铭：愿宽容如烈火带来温暖，愿善良之人终得一切恩露之报。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53511] = "{id = 53511, NameZh = '骑士的羽穗', Icon = 'item_45089', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23112, AdventureValue = 85, AdventureReward = {rewardid = {870012}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【古城末日“叛变者”调查档案12号】欧律皮罗斯男爵家族特有的象征，它被拔下来，伴随着卡珊德拉公主度过了病患余生。男爵在破城之日，于公主墓碑前刎颈自杀，当时他手中握着这根羽穗，在墓碑前的地上用剑刻下了愧疚之言。男爵倒在血泊中时，羽穗随风飘远，爱意随风而逝。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53512] = "{id = 53512, NameZh = '宰相的怀表', Icon = 'item_task_31', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23113, AdventureValue = 85, AdventureReward = {rewardid = {870013}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【古城末日“叛变者”调查档案13号】奥德修斯宰相的怀表，是他非常珍爱之物。据说是妻子的遗物，宰相日理万机时总是打开它确认时间，从未让它离开过身边。但它现在却没有陪伴着宰相的遗体，是有人将它偷走了吗？怀表本身价值不菲，曾经有人向宰相高价收取这块怀表，当然被拒绝了。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53513] = "{id = 53513, NameZh = '贵妇的礼帽', Icon = 'item_task_32', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23114, AdventureValue = 85, AdventureReward = {rewardid = {870014}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【古城末日“叛变者”调查档案14号】海因里希执政期间，曾因为这顶礼帽而闹出过一段绯闻，它原先被摆放在海因里希的书房中，后来却出现在了一位贵妇的头上。不过严肃古板的执政官阁下，可不像是会偷偷赠送贵妇礼帽的那种男人啊，大家都认为他这辈子唯一的情人就是他的工作。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53514] = "{id = 53514, NameZh = '国王的宝剑', Icon = 'item_40332', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23115, AdventureValue = 85, AdventureReward = {rewardid = {870015}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【古城末日“叛变者”调查档案15号】初代施密特国王使用过的剑，此后，每一任国王都会在加冕仪式上接过它。它见证了施密特王族的兴衰。最后一任国王将它留在了克雷斯特汉姆古城，远赴他乡再也没有回来。海因里希执政官总是遥遥望着这把国王宝剑叹息。希默梅思用它斩下了海因里希的头颅，然后随手把它丢弃了，于是它所承载的一代王朝落下帷幕。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53515] = "{id = 53515, NameZh = 'GLC.A031号档案', Icon = 'task_certificate', Type = 63, Quality = 4, MaxNum = 9999, SellPrice = 100, AdventureSort = 23116, AdventureValue = 143, AdventureReward = {rewardid = {870016}}, StorageReward = _EmptyTable, Condition = 64, Desc = 'A.W800年，希默梅思带着不死军团攻入克雷斯特汉姆，皇家军团缚手难敌，王城一夜之间变为死城。有人认为，皇家中有“叛变者”向希默梅思泄露军情。一位秘密调查员根据仅存下来的皇家文献，锁定了十五个嫌疑人，并将这十五个人的资料编集为【古城末日“叛变者”档案】。档案中关于“叛变者”的真实身份并未明确提及，但从那些相关物品的信息中可寻得蛛丝马迹。档案详情在春楠那里可以复查。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53516] = "{id = 53516, NameZh = '毒药', Icon = 'item_5021', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23201, AdventureValue = 98, AdventureReward = {rewardid = {870017}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【楔子】许多故事的开始，都是伴随着阴谋、凶杀、冤情、争斗。那天晚上，莫里茨公爵冰冷地躺在床上，脚边倒着一瓶毒药。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53517] = "{id = 53517, NameZh = '魔眼', Icon = 'item_7337', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23202, AdventureValue = 98, AdventureReward = {rewardid = {870018}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【憎恶之书·公正】“是献出你的灵魂，还是让我夺走你朋友的生命，做出选择吧！”恶魔用它那似乎能洞察人类内心的眼睛，微笑着流露出狰狞的目光，对面前的驱魔人说道。卡伦赞德正是被这双魔眼所蛊惑。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53518] = "{id = 53518, NameZh = '符文', Icon = 'item_12004', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23203, AdventureValue = 85, AdventureReward = {rewardid = {870019}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【憎恶之书·真相】驱魔人的符上，狂乱的字迹流露出书写者内心的挣扎与不安，是用力量维护公正，还是用力量解救落难的生命？驱魔人明亮的眼睛能看透黑暗，却看不透世间的正义。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53519] = "{id = 53519, NameZh = '钥匙', Icon = 'item_40011', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23204, AdventureValue = 85, AdventureReward = {rewardid = {870020}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【恐惧之书·孤独】阴冷的监狱中，往往隐藏着禁忌、罪恶的故事。而用来开启它们的钥匙，就像是巫婆的苹果。至情至义的驱魔人知道这样做违背了法律与秩序，但他还是用钥匙打开了牢门，放走了他最好的朋友。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53520] = "{id = 53520, NameZh = '手铐', Icon = 'item_5042', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23205, AdventureValue = 85, AdventureReward = {rewardid = {870021}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【恐惧之书·真相】牢房狭小的窗洞漏进清冷的月光，当骄傲的驱魔人失去自由之时，仿佛也失去了所有，包括自己存在的价值。链铐的响动在提醒着他犯下的错误。他无法向任何人倾诉苦衷，因为是他自己将自己推进了孤独的深渊。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53521] = "{id = 53521, NameZh = '粮食', Icon = 'item_12305', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23206, AdventureValue = 85, AdventureReward = {rewardid = {870022}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【怯懦之书·仁慈】放置在我们眼前的普通食物，困苦的时候，它会比金子更珍贵渴求，而富裕之时，它却廉价得仿佛脚边的野草，随意可践踏忽视。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53522] = "{id = 53522, NameZh = '金币', Icon = 'item_105', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23207, AdventureValue = 98, AdventureReward = {rewardid = {870023}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【怯懦之书·真相】金钱就像是这世间的一切诱惑，过度纵容，就会导致极端的后果。为了满足逐渐贪得无厌的卡伦赞德的要求，驱魔人放弃了一切，而这并没有带来善果。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53523] = "{id = 53523, NameZh = '书卷', Icon = 'item_42519', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23208, AdventureValue = 85, AdventureReward = {rewardid = {870024}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【嫉妒之书·睿智】古列弗深爱书籍，他相信那是它最忠诚而值得信赖的朋友。因此，他有一颗博大精深的头脑，总是能想出绝妙的主意扭转乾坤。但这并没有给他带来好下场。智者千虑却必有一失。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53524] = "{id = 53524, NameZh = '冕冠', Icon = 'item_45046', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23209, AdventureValue = 85, AdventureReward = {rewardid = {870025}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【嫉妒之书·真相】卡伦赞德的野心愈加庞大，最后，他看向了那顶璀璨的冕冠。华丽的外表下，早已是枯朽的灵魂。他泯灭了一切良善，冷酷无情得就像一个真正的恶魔。欲望的终点，往往是穷途末路。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53525] = "{id = 53525, NameZh = '利刃', Icon = 'item_40305', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23210, AdventureValue = 98, AdventureReward = {rewardid = {870026}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【尾声】驱魔人行走在夜间，以狩猎魔鬼为职业的他，今晚要去狩猎一个真正的恶魔。他握着锋利的武器，内心默念：“卡伦赞德，你曾是我最重要的朋友，所以也必须由我来洗净你的罪孽。而我将背负罪孽，永生潜行在黑夜中。”', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53526] = "{id = 53526, NameZh = 'GLC.X114号档案', Icon = 'task_certificate', Type = 63, Quality = 4, MaxNum = 9999, SellPrice = 100, AdventureSort = 23211, AdventureValue = 143, AdventureReward = {rewardid = {870027}}, StorageReward = _EmptyTable, Condition = 64, Desc = '记录了四个互为挚友并深受国王恩宠的驱魔人互相间打了一个赌，然而谁也没有真正在意这场赌局。但命运的嘲讽却悄然降临在他们身上，为了挽救他们的好友卡伦赞德，四人使用了不同的方法，却酿成了一场惨痛悲剧。档案详情在影子驱魔人那里可以复查。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53527] = "{id = 53527, NameZh = '初见的华尔兹', Icon = 'item_48545', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23301, AdventureValue = 98, AdventureReward = {rewardid = {870028}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【第一乐章】假面舞会上的面具。它记录着桂瑟兰妮与加布里尔在假面之下开始的一段美丽华尔兹。初见之时，未知的一切，华丽的盛装，彼此的吸引，在富丽堂皇的舞池中相映生辉，童话一般的美好而浪漫。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53528] = "{id = 53528, NameZh = '玫瑰的踢踏舞', Icon = 'item_task_16', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23302, AdventureValue = 98, AdventureReward = {rewardid = {870029}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【第二乐章】凋零的荆棘玫瑰。它被收藏在加布里尔的书桌右手边第一格抽屉中，制成了精巧的干花，夹在一本名为《梦中婚礼》的书中。当死讯传来之时，桂瑟兰妮亲手将它放进了加布里尔的遗棺中。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53529] = "{id = 53529, NameZh = '爱情的圆舞曲', Icon = 'item_40331', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23303, AdventureValue = 98, AdventureReward = {rewardid = {870030}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【第三乐章】细长锋利的西洋剑。桂瑟兰妮收到这件礼物时，不知该夸赞将军心意别致还是叹息他不懂情调。很长一段时间，它都被挂在墙上当一件精致的装饰品，直到桂瑟兰妮把它拿下来，放到长眠的将军身边，并说：“谢谢你的守护。”', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53530] = "{id = 53530, NameZh = '月光的小夜调', Icon = '22', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23304, AdventureValue = 98, AdventureReward = {rewardid = {870031}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【第四乐章】远方而来的战讯。“血色胜利”之战，王国军于险恶重重中获得艰难的胜利，战讯传至王城，桂瑟兰妮在侯爵府邸落下悲伤的眼泪，懊悔凝结在无声泪水中。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53531] = "{id = 53531, NameZh = '教堂的圣福音', Icon = 'item_52304', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23305, AdventureValue = 98, AdventureReward = {rewardid = {870032}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【第五乐章】绅士的手帕。修道院中，忏悔的言语有谁在聆听？抑郁的容颜是为谁思念？绅士迈着轻轻的脚步靠近正在祈祷的女子身边，递出手帕款款温柔说：“请不要因为后悔而哭泣，它不适合你。我亲爱的桂瑟兰妮。”', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53532] = "{id = 53532, NameZh = '王子的嘲笑声', Icon = 'item_52316', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23306, AdventureValue = 98, AdventureReward = {rewardid = {870033}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【第六乐章】碎裂的镜子。王子在富丽堂皇的舞池中央丢下假面，戏谑嘲笑道：“来不及了，你这个贪心势利的女人。破镜永不能恢复原貌。”璀璨迷人的水晶灯光下，童话似乎就在眼前破碎，再不会有浪漫的华尔兹。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53533] = "{id = 53533, NameZh = '梦中的婚礼章', Icon = 'item_45198', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23307, AdventureValue = 98, AdventureReward = {rewardid = {870034}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【第七乐章】枯黄的落叶。“如果，我不能在神父的面前吻你，幸福永不会降临在你我身上。”表达着如此爱意的人，在希默梅思的降临中，永远沉浸入梦中的婚礼。落叶带走生命的韵律。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53534] = "{id = 53534, NameZh = 'GLC.E109号档案', Icon = 'task_certificate', Type = 63, Quality = 4, MaxNum = 9999, SellPrice = 100, AdventureSort = 23308, AdventureValue = 143, AdventureReward = {rewardid = {870035}}, StorageReward = _EmptyTable, Condition = 64, Desc = '记录了克雷斯特汉姆古城灭亡前夕举行的一场盛大婚礼。桂瑟兰妮与加布里尔王子因偏见产生重重误会，又因爱而最终携手，然而就在婚礼举行之时，希默梅思降临，将一切美好带入了永不会醒来的梦境中。档案详情在墓地守夜人那里可以复查。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53535] = "{id = 53535, NameZh = '休密兹的王冠', Icon = 'item_task_25', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23309, AdventureValue = 117, AdventureReward = {rewardid = {870036}}, StorageReward = _EmptyTable, Condition = 64, Desc = '暴君休密兹花费巨资打造的王冠，上面镶嵌着两种顶级的宝石，更有符文大师在其上铭刻符文，是无价之宝。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53536] = "{id = 53536, NameZh = '水晶残片', Icon = 'item_5026', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23117, AdventureValue = 98, AdventureReward = {rewardid = {870037}}, StorageReward = _EmptyTable, Condition = 64, Desc = '寄托着莫里森与盖妮爱情的留影水晶，随着盖妮的逝去，留影水晶中的能量也流逝殆尽，成为一片华贵的水晶残片。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53537] = "{id = 53537, NameZh = '导师资格证', Icon = 'task_certificate', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 22001, AdventureValue = 108, AdventureReward = {rewardid = {870039}}, StorageReward = _EmptyTable, Condition = 64, Desc = '由隶属于卢恩·米特加尔德王国的冒险者公会颁发，象征着责任和荣誉的导师资格证书，那么从今天起，可爱的初心者就交给你来教导了！', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53538] = "{id = 53538, NameZh = '来自B格猫的贺卡', Icon = 'item_500510', Type = 63, Quality = 5, MaxNum = 9999, SellPrice = 100, AdventureSort = 20201, AdventureValue = 90, AdventureReward = {rewardid = {870038}}, StorageReward = _EmptyTable, Condition = 64, Desc = '一份来自B格猫的新年贺卡嘿，冒险者们。新年的钟声敲响了，B格猫星人集体祝福大家新年快乐，鸡年大吉！ 这是我们一起过的第一个农历春节，2017年我们还想和大家一起过很多很多的节日。目前我们仍有很多不足，我们也在努力，努力与大家一同构筑这美好的大世界呀！感谢大家的支持与包容，送上一份小小心意，给你拜年啦！-----------<B格猫星球住民联名书写>', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53539] = "{id = 53539, NameZh = '伏尔松格家徽', Icon = '16', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 22901, AdventureValue = 82, AdventureReward = {rewardid = {870040}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【星尘之光·晓】这是一个伟大的家族，它开启了史诗传奇，书写了英雄之歌，世间传唱着这个家族成员无数的美誉佳绩。然而就像创世神给这个世界定下的法则：没有任何东西是不会衰亡的。奥丁神给了这个家族启示，从此也在这个家族中埋下了火种，致使她一步步走向了灭亡。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53540] = "{id = 53540, NameZh = '希格尼的计谋', Icon = '19', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 22902, AdventureValue = 82, AdventureReward = {rewardid = {870041}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【星尘之光·暗】伏尔松格家族中最明亮的星，她的诞生仿佛就是为了给这个家族带来希望与变化。当家破人亡，她和哥哥沦陷于苦难中时，她不惜触犯不可原谅的禁忌罪恶，只为替深爱的家族手刃仇敌。美丽的外表下，她拥有一颗精于计算的头脑和坚贞的心，连她的哥哥西格蒙德也被她计算了。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53541] = "{id = 53541, NameZh = '辛菲奥特利之勇', Icon = 'item_42030', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 22903, AdventureValue = 82, AdventureReward = {rewardid = {870042}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【星尘之光·耀】希格尼触犯禁忌生下的孩子，她期待着这个孩子长大成人后为家族复仇。尽管生来便背负着沉重的命运，但在母亲精心的教养下，他拥有人们穷尽想象力所能赋予的美好品格，是星尘之中最耀眼的那颗星。世间没有一种苦难，能令他感到害怕。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53542] = "{id = 53542, NameZh = '格拉姆神剑', Icon = 'item_40716', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 22904, AdventureValue = 82, AdventureReward = {rewardid = {870043}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【星尘之光·武】奥丁留下的启示之剑。它的剑刃两面，一面刻着“博爱之仁”，相传由它的第一任主人西格蒙德所刻，它帮助伏尔松格家族手刃了仇敌，使这个家族在绝境中重生；另一面刻着“正义之威”，相传由它的第二任主人希格尔德重铸这把剑时所刻，但这一次，它却为它的主人招来了厄运。希格尔德在睡觉时将它置于自己和爱人之间，令他的爱人布伦希尔德产生了误会。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53543] = "{id = 53543, NameZh = '古德露恩的爱意', Icon = 'item_52702', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 22905, AdventureValue = 82, AdventureReward = {rewardid = {870044}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【星尘之光·爱】古德露恩的出现让希格尔德开始走向悲剧的命运。在魔法药水的控制下，她得到了希格尔德的妻子之名，却始终没有得到希格尔德的真心。虚幻的爱情，注定了他们的婚姻不会有好结局。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53544] = "{id = 53544, NameZh = '布伦希尔德火焰', Icon = 'item_47018', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 22906, AdventureValue = 82, AdventureReward = {rewardid = {870045}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【星尘之光·焰】布伦希尔德是一名瓦尔基里。世间有许多歌谣传唱着她与希格尔德的凄美爱情，那些歌词叙述了即便奥丁在布伦希尔德身周设下烈火，也无法阻止希格尔德冲破火焰与布伦希尔德相爱。只是爱情的开端虽浪漫美好，终敌不过岁月的磨砺。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53545] = "{id = 53545, NameZh = '希格尔德之死', Icon = 'item_task_20', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 22907, AdventureValue = 82, AdventureReward = {rewardid = {870046}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【星尘之光·陨】死于暗杀者之手的希格尔德，恐怕没有想到自己会因爱情而引来杀身之祸。在临终前，他终于醒悟到了布伦希尔德的自私。他用微弱的气息安慰着在他身边哭泣的妻子古德露恩，然后咽下了最后一口气。生与死，从来无法做第二次选择。伏尔松格家族因爱而崛起，也因爱而陨落。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53546] = "{id = 53546, NameZh = '尼伯龙根指环', Icon = 'item_40017', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 22908, AdventureValue = 82, AdventureReward = {rewardid = {870047}}, StorageReward = _EmptyTable, Condition = 64, Desc = '【星尘之光·惑】佩戴在漆黑部队首领左手食指上的指环，它曾经是希格尔德与古德露恩的订婚戒指。从英灵殿归来，它将追随它的主人统御龙族。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53547] = "{id = 53547, NameZh = 'GLC.A014号档案', Icon = 'task_certificate', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 22909, AdventureValue = 113, AdventureReward = {rewardid = {870048}}, StorageReward = _EmptyTable, Condition = 64, Desc = '又名“星尘档案”。它记录着隐匿于龙谷中的神秘组织“漆黑部队”的来历。这些以女武神头盔为标识的战士，曾经都是《尼伯龙根指环》史诗中的英雄人物，他们从英灵殿返回人间，守护着龙谷，行踪神秘。犹如星尘，用微光照亮黑暗。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53548] = "{id = 53548, NameZh = '腐烂的骨骼', Icon = 'item_53548', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23516, AdventureValue = 85, AdventureReward = {rewardid = {870074}}, StorageReward = _EmptyTable, Condition = 64, Desc = '古尔顿有着令人羡慕的体质，他丝毫不受瘟疫的影响，国王得知后，请求古尔顿能够舍生取义，成为救王国于瘟疫之中的大英雄，古尔顿答应了。数名大魔法师用魔法抽取瘟疫，尽数注入到古尔顿的身体之中。', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53549] = "{id = 53549, NameZh = '肮脏的花冠', Icon = 'item_53549', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23517, AdventureValue = 85, AdventureReward = {rewardid = {870075}}, StorageReward = _EmptyTable, Condition = 64, Desc = '在古尔顿被王国赶出城市后，卡丽莎不辞辛苦的找到古尔顿，质问古尔顿是否抛弃了她，当他看到古尔顿身上令人作呕的瘟疫时，才明白了一切。', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53550] = "{id = 53550, NameZh = '国王的权杖', Icon = 'item_53550', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23519, AdventureValue = 98, AdventureReward = {rewardid = {870077}}, StorageReward = _EmptyTable, Condition = 64, Desc = '国王的反应让古尔顿失望透顶，他摘下了自己的面具，瘟疫瞬间夺取了国王与事务官的生命，他知道他的好友凯莱尔就在他身边，他的仇已经报了，卡丽莎的死去对他打击很大，他哀求着凯莱尔给予他致命的一击…', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53551] = "{id = 53551, NameZh = '破损的绞绳', Icon = 'item_53551', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23518, AdventureValue = 85, AdventureReward = {rewardid = {870076}}, StorageReward = _EmptyTable, Condition = 64, Desc = '卡丽莎从古尔顿的小屋中离开，没想到王国事务官已经在门口等待着她，事务官以怀疑她身上携带着瘟疫为由，对卡丽莎实施了绞刑。', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53552] = "{id = 53552, NameZh = 'GLC.X157号档案', Icon = 'task_certificate', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23520, AdventureValue = 117, AdventureReward = {rewardid = {870078}}, StorageReward = _EmptyTable, Condition = 64, Desc = '一场突如其来的瘟疫，改变了古尔顿的一生。古尔顿拥有对瘟疫的超强抵抗力，这使得他成为解决这次灾难的唯一人选，于是王国利用超魔导士的力量，把几乎所有的瘟疫都集中在古尔顿身上。古尔顿攒钱给卡丽莎制作了花冠，古尔顿的心情无疑是非常开心的。但是好景不长，古尔顿身患瘟疫，不得不从人口密集的城市中搬出来。卡丽莎锲而不舍得找寻着古尔顿，终于在城外的密林处找到了他。古尔顿惊恐着，让卡丽莎不要过来，但是卡丽莎却毫不在意…好人难得好报，古尔顿看错了人心。当危险过后，人们会把所有危险都扼杀在摇篮里，生怕自己再受到伤害，人就是这样的生物。当卡丽莎在绞刑架上大声斥责时，这些冷漠的人对此毫无羞愧之心。而大部分民众也是在冷眼旁观，甚至拍手称快。卡丽莎的逝去对古尔顿造成了相当大的打击。古尔顿选择了报复，而这报复又在情理之中。他释放了自己瘟疫，经过压抑的瘟疫爆发起来更加凶残。顷刻之间国王与王国事务官便双双毙命。事情的最后他让他的挚友凯莱尔带走了他的生命，使瘟疫从此消失。', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53553] = "{id = 53553, NameZh = '古尔顿的日记[1]', Icon = 'item_3002', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23512, AdventureValue = 98, AdventureReward = {rewardid = {870070}}, StorageReward = _EmptyTable, Condition = 64, Desc = '一个名叫古尔顿的日记，短短几页的纸张记载着骇人听闻的瘟疫爆发事件。古尔顿的友人在这场瘟疫中去世，古尔顿怀疑这次瘟疫的爆发是因为他们曾在下水道工作，被蓝鼠袭击造成的。', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53554] = "{id = 53554, NameZh = '古尔顿的日记[2]', Icon = 'item_3002', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23513, AdventureValue = 98, AdventureReward = {rewardid = {870071}}, StorageReward = _EmptyTable, Condition = 64, Desc = '一个名叫古尔顿的日记，短短几页的纸张记录着与一位名叫卡丽莎的女孩在一起的愉快时光，在卡丽莎的生日到来之际，他精心准备着自己的礼物，想象着未来两人的幸福生活，而愉快的时光总是短暂的……', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53555] = "{id = 53555, NameZh = '古尔顿的日记[3]', Icon = 'item_3002', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23514, AdventureValue = 98, AdventureReward = {rewardid = {870072}}, StorageReward = _EmptyTable, Condition = 64, Desc = '卡丽莎已经很久没有来看望城外的古尔顿了，这使得古尔顿异常伤心，古尔顿决心去质问卡丽莎究竟发生了什么，却得到了一个令他无法接受的消息。', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53556] = "{id = 53556, NameZh = '古尔顿的日记[4]', Icon = 'item_3002', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23515, AdventureValue = 85, AdventureReward = {rewardid = {870073}}, StorageReward = _EmptyTable, Condition = 64, Desc = '古尔顿决心要报复，他放弃携带友人送给他可以抑制瘟疫的面具，他决心要让那些恶毒的伪善者尝尝死亡的恐惧。', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53557] = "{id = 53557, NameZh = '皇室徽章（暴食者）', Icon = 'item_60002', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23521, AdventureValue = 98, AdventureReward = {rewardid = {870079}}, StorageReward = _EmptyTable, Condition = 64, Desc = '二王子殿下失踪之后，王宫附近出现了一个诅咒魔人，与他近距离接触的人们都遭受到了诅咒，那些民众变成了暴食的魔物，这让大王子下定了决心，无论这个诅咒魔人与二王子殿下有什么联系，都一定要消灭他！大王子的最忠贞的骑士代顿带着命令出发…', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53558] = "{id = 53558, NameZh = '皇室徽章（腐朽者）', Icon = 'item_60003', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23522, AdventureValue = 98, AdventureReward = {rewardid = {870080}}, StorageReward = _EmptyTable, Condition = 64, Desc = '三王子殿下失踪了，于此同时，王宫附近出现了一个诅咒魔人，任何他去过的地方，所有物品都将腐朽，民众们的血汗钱从此化为尘埃，爱民如子的大王子当然不会允许这种事情发生，他命令骑士代顿一定要解除民众的苦痛…', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53559] = "{id = 53559, NameZh = '皇室徽章（争斗者）', Icon = 'item_60005', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23523, AdventureValue = 98, AdventureReward = {rewardid = {870081}}, StorageReward = _EmptyTable, Condition = 64, Desc = '诅咒魔人出现了，他的诅咒带来了一次次争斗，王都因为魔人的诅咒，流血事件不断发生，大王子决定让骑士代顿惩治这个邪恶的魔人，而大王子才发现，自己的四弟出门狩猎已经许久没有消息传出来了…', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53560] = "{id = 53560, NameZh = '伪善者的假面', Icon = 'item_48554', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23524, AdventureValue = 85, AdventureReward = {rewardid = {870082}}, StorageReward = _EmptyTable, Condition = 64, Desc = '当仅剩的五王子逃出了皇宫，大王子对外宣称发现第四位诅咒魔人，再次命令骑士代顿前往消灭五王子，然而对大王子产生疑心的代顿并没有第一时间消灭所谓的诅咒魔人。五王子告知代顿，他的三位哥哥都是死于大王子的阴谋。代顿曾经手刃三位王子，代顿已经违背了自己的骑士精神，他决定要为自己的罪行负责，但是再此之前，他还有事情要做…', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53561] = "{id = 53561, NameZh = '心中的魔鬼', Icon = 'item_52507', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23525, AdventureValue = 98, AdventureReward = {rewardid = {870083}}, StorageReward = _EmptyTable, Condition = 64, Desc = '权力是魔鬼，大王子被魔鬼所诱惑，陷害了三位王子，而他的恶行终于被自诩正义的骑士代顿发现了，代顿无法原谅这样的自己，也无法理解效忠的王子，他明白，大王子一旦继承王位，这对国家来说并不是好事，他决定违背骑士精神的最后一条——忠诚！大王子死后，代顿带回被他藏起来的五王子，告诫五王子，权力是每人心中的恶魔，一定要战胜这个魔鬼！得到五王子承诺的代顿在这大厅自刎结束了自己的一生。', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53562] = "{id = 53562, NameZh = 'GLC.X163号档案', Icon = 'task_certificate', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23526, AdventureValue = 117, AdventureReward = {rewardid = {870084}}, StorageReward = _EmptyTable, Condition = 64, Desc = '王宫的二王子离奇失踪后，王都出现了一个名为“暴食者”的诅咒魔人。骑士代顿奉大王子的命令前去讨伐。不久后，三王子殿下失踪了，与此同时，王宫附近出现了一个诅咒魔人，名为“腐朽者”。爱民如子的大王子绝对不会允许他为祸王都，骑士代顿再次奉命讨伐。接着诅咒魔人“争斗者”出现了，他的诅咒带来了一次次争斗，王都因为魔人的诅咒，流血事件不断发生。大王子决定让骑士代顿惩治这个邪恶的魔人，而与此同时，四王子出门狩猎已经许久没有消息传出来了。大王子对外宣称发现第四位诅咒魔人，再次命令骑士代顿前往消灭诅咒魔人。代顿这才知道，这一切，都是大王子为了继承王位所使的阴谋。对大王子产生疑心的代顿并没有第一时间消灭所谓的诅咒魔人。五王子告知代顿，他的三位哥哥都是死于大王子的阴谋。权力是魔鬼，大王子被魔鬼所诱惑，陷害了三位王子，代顿作为大王子手中的利剑，自然有着无法推卸的责任。代顿决定背叛自己所信奉的骑士精神——忠诚！为了维护自己心中的正义，他要杀死大王子！大王子死后，代顿带回被他藏起来的五王子，告诫五王子，权力是每个人心中的恶魔，一定要战胜这个魔鬼！五王子含泪答应了代顿的请求，而代顿在这大厅自刎结束了自己的一生。', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53563] = "{id = 53563, NameZh = '米莫斯的战袍', Icon = 'item_43020', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23401, AdventureValue = 117, AdventureReward = {rewardid = {870049}}, StorageReward = _EmptyTable, Condition = 64, Desc = '“米莫斯知道为了守护自己重要的人，只有做好恶性结局的打算，不沮丧逃避，以自己的力量勇往直前，战斗不止！”', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53564] = "{id = 53564, NameZh = '艾琳娜丝的四叶草', Icon = 'item_52302', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23402, AdventureValue = 98, AdventureReward = {rewardid = {870050}}, StorageReward = _EmptyTable, Condition = 64, Desc = '“没想到在这里也能找到四叶草，我上一次见到还是在家乡的南部森林里找到的，真的超兴奋呢！对了，我今天看到苏米娜偷偷的买了紫色的染料，难道她也想把头发染成和我一样的颜色？真讨厌呢，我最不喜欢别人学我了”-艾琳娜丝', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53565] = "{id = 53565, NameZh = '亚米拉的入团日记', Icon = 'item_3608', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23403, AdventureValue = 98, AdventureReward = {rewardid = {870051}}, StorageReward = _EmptyTable, Condition = 64, Desc = '“这些盔甲虽然看上去很笨重，但是我还是相信它会保护我，保护这座城堡，那么亚米拉，加油吧！”-亚米拉', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53566] = "{id = 53566, NameZh = '苏米娜的染料', Icon = 'item_12104', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23404, AdventureValue = 98, AdventureReward = {rewardid = {870052}}, StorageReward = _EmptyTable, Condition = 64, Desc = '“训练时站在我前排的艾琳娜丝真特别，尤其是她的紫色头发，虽然平日里天天需要带着头盔，不过我还是想找个时间把头发也染成紫色”-苏米娜', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53567] = "{id = 53567, NameZh = '艾思丽的徽章', Icon = 'item_52319', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23405, AdventureValue = 98, AdventureReward = {rewardid = {870053}}, StorageReward = _EmptyTable, Condition = 64, Desc = '“白骑士团的哈维队长昨天送了这个徽章给我，有点小兴奋呢，虽然我料到他会有所表示，但是这突如其来的惊喜还是有点让我手足无措呢”。', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53568] = "{id = 53568, NameZh = '灵魂印记', Icon = 'item_task_07', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23406, AdventureValue = 98, AdventureReward = {rewardid = {870054}}, StorageReward = _EmptyTable, Condition = 64, Desc = '在神使现身克雷斯特汉姆之前就有一些神智奇特的战士陷入了疯狂，艾里维奇便是其中之一，在众人协力击溃了他的时候，有一位炼金术师使用贤者之石将他的灵魂捆绑在了他的盔甲里，从此后“艾里维奇”成了一副只会守卫城堡的疯剑士。', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53569] = "{id = 53569, NameZh = '未送出手的情书', Icon = 'item_task_34', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23407, AdventureValue = 98, AdventureReward = {rewardid = {870055}}, StorageReward = _EmptyTable, Condition = 64, Desc = '柯雷罗在刚刚来到克雷斯特汉姆时就爱上了一位姑娘，在得知了她是一名皇室公主后，放下了表白的念头，而手中的书信似乎也永远送不出手了。', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53570] = "{id = 53570, NameZh = '金箭', Icon = 'item_task_35', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23408, AdventureValue = 98, AdventureReward = {rewardid = {870056}}, StorageReward = _EmptyTable, Condition = 64, Desc = '克雷斯特汉姆中有名的工匠设计出的金色箭矢，在著名游侠奥兹玛手中大放光彩。在黑暗魔物侵袭之战中，奥茨玛带着他心爱的金箭，深入黑暗魔物的领地寻找魔物头领，从此再也没有他的消息。', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53571] = "{id = 53571, NameZh = '诅咒魔弓', Icon = 'item_41217', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23409, AdventureValue = 98, AdventureReward = {rewardid = {870057}}, StorageReward = _EmptyTable, Condition = 64, Desc = '艾克林曾经希望自己成为一名真正的魔法师，他并没有天赋，所幸艾克林有一手好箭术，日子还算过得下去。直到某次探险，艾克林得到了一把魔弓，可以在弓箭上施加强大的魔法，艾克林欣喜若狂。每次使用魔弓的力量，艾克林的灵魂就会被魔弓吸取一点，直到某天艾克林成为了没有灵魂的行尸走肉，诅咒魔弓之名也随着艾克林的逝去而流传开来。', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53572] = "{id = 53572, NameZh = '无形箭', Icon = 'item_12503', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23410, AdventureValue = 117, AdventureReward = {rewardid = {870058}}, StorageReward = _EmptyTable, Condition = 64, Desc = '此箭早已存在，只是无形、无迹，箭矢早已存于天地间，只等待时机凝聚起来，只到国难当头的那一刻，任何勇士只要心中有勇气都能射出此箭。', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53573] = "{id = 53573, NameZh = '妖气', Icon = 'item_47018', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23501, AdventureValue = 98, AdventureReward = {rewardid = {870059}}, StorageReward = _EmptyTable, Condition = 64, Desc = '村正曾在名师手下学习剑道，却因为师傅偏爱自己的师兄嫉火中烧，误入歧途亲手杀害了自己的师傅和师兄后，因为愧疚切腹，含恨化作一股凌冽的妖气。', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53574] = "{id = 53574, NameZh = '蛇鳞', Icon = 'item_52315', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23502, AdventureValue = 98, AdventureReward = {rewardid = {870060}}, StorageReward = _EmptyTable, Condition = 64, Desc = '古城中曾有一条无恶不作的八歧大蛇，在城中掳走妇女和儿童作为它的口中食粮，终于触犯了天神，天神赐给城中最勇敢的战士一把天业云剑去讨伐这条大蛇，将其削皮拔骨，并取其蛇鳞来祭奠被大蛇迫害的无辜的城民们。', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53575] = "{id = 53575, NameZh = '国纲的印记', Icon = 'item_task_07', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23503, AdventureValue = 98, AdventureReward = {rewardid = {870061}}, StorageReward = _EmptyTable, Condition = 64, Desc = '古时，有个有名的盗贼拥有一件神奇的羽衣，披上羽毛即变成鸟，脱下羽毛就能现身。如果那个富豪的家庭的防卫没做到位。，那么一旦被他所发现，就会在窗台上留下两滴血作为目标印记。', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53576] = "{id = 53576, NameZh = '须佐之男的思念', Icon = 'item_52408', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23504, AdventureValue = 98, AdventureReward = {rewardid = {870062}}, StorageReward = _EmptyTable, Condition = 64, Desc = '古城战火四溢时，须佐之男将一把十握剑交给妻子防身后便出门从军去了，战后回到家中发现妻子因为重病还是去世了，一想到自己在妻子病重时不能守护在左右后悔莫及，只留下当初那帮十握剑孤独的靠在房角。', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53577] = "{id = 53577, NameZh = '八犬士的勇气', Icon = '27', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23505, AdventureValue = 98, AdventureReward = {rewardid = {870063}}, StorageReward = _EmptyTable, Condition = 64, Desc = '灾难之神降下灾祸，使古城生灵涂炭，提出要求将八位壮汉的灵魂交出才能平息灾难，国王发下昭示派了城中八位威武勇猛的士兵，这八位士兵明知自己将会有去无回，还是毅然决然的决定为了城中的百姓牺牲自己。国王为了纪念他们修建了八座雄伟的石碑取名为八犬士的勇气。', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53578] = "{id = 53578, NameZh = '生命魔剑', Icon = 'item_40736', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23506, AdventureValue = 117, AdventureReward = {rewardid = {870064}}, StorageReward = _EmptyTable, Condition = 64, Desc = '生命魔剑是一柄有战斗欲望的剑，剑拔出来之后如果不沾血就不可以入鞘。剑 的拥有者必须尊重剑的意愿，不然魔剑就会背叛主人、任意杀戮。', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
