	[53582] = "{id = 53582, NameZh = '宝剑', Icon = 'item_40336', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23510, AdventureValue = 98, AdventureReward = {rewardid = {870068}}, StorageReward = _EmptyTable, Condition = 64, Desc = '代表元素风，象征思想、智慧、交流和冲突。', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53583] = "{id = 53583, NameZh = '命运纸牌', Icon = '19', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23511, AdventureValue = 117, AdventureReward = {rewardid = {870069}}, StorageReward = _EmptyTable, Condition = 64, Desc = '过去有位通晓过去未来的魔法师，却得不到国王的器重，预言了古城的毁灭后居然还惨遭王国的追杀，他悲愤的留下一张命运的纸牌后就消失了，再也没人见过他的行踪。', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53584] = "{id = 53584, NameZh = '《古城轶事》', Icon = 'item_42519', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23527, AdventureValue = 98, AdventureReward = {rewardid = {870085}}, StorageReward = _EmptyTable, Condition = 64, Desc = '一位名叫切尔西的人撰写的书籍，上面详细的记载了切尔西和女伴妮卡在受到魔物袭击时，一位“黑武士”出面救下了他的全过程。后来被切尔西记载，“黑武士”之名由此广为传播。', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53585] = "{id = 53585, NameZh = '生锈的铁戒', Icon = 'item_44012', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23528, AdventureValue = 98, AdventureReward = {rewardid = {870086}}, StorageReward = _EmptyTable, Condition = 64, Desc = '仰慕“黑武士”之名的战斗狂人阿尔顿斯带着他心爱的剑戒——铁牙挑战“黑武士”，在“黑武士”的进攻之下，阿尔顿斯毫无还手之力，剑戒铁牙也在战斗中分离。', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53586] = "{id = 53586, NameZh = '断裂的项链', Icon = 'item_44004', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23529, AdventureValue = 98, AdventureReward = {rewardid = {870087}}, StorageReward = _EmptyTable, Condition = 64, Desc = '爱德华送给妮卡的项链，在一次魔物袭击中断裂，爱德华独自引开魔物，使得妮卡成功逃离了魔物的包围，但是爱德华再也没有回来。', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53587] = "{id = 53587, NameZh = '黑暗心核', Icon = 'dark_001', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23530, AdventureValue = 85, AdventureReward = {rewardid = {870088}}, StorageReward = _EmptyTable, Condition = 64, Desc = '慌不择路逃命的爱德华来到了一处洞穴，洞穴中似乎有什么在威慑着那些魔物，它们不敢靠近洞穴。爱德华不断深入洞穴，发现了一具巨大的尸体，在尸体中找到了一颗散发着迷人香气的心核，爱德华在饥饿的煎熬下，吃下了这颗心核，整个人发生了巨大的变化。成为了拥有神秘黑暗力量的人类。', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53588] = "{id = 53588, NameZh = '黑武士徽章', Icon = 'item_42512', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23531, AdventureValue = 85, AdventureReward = {rewardid = {870089}}, StorageReward = _EmptyTable, Condition = 64, Desc = '爱德华成为了黑武士后，妮卡的项链在他的黑暗力量浸染下成为了他独特的黑暗武士徽章。他认为光明与黑暗只在一念之间，他拥有黑暗力量，但他的心却向往着光明。在黑暗生物的进攻战役中完成了他的使命，而黑暗武士徽章成为了他唯一存在过的证据。', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53589] = "{id = 53589, NameZh = 'GLC.X185号档案', Icon = 'task_certificate', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23532, AdventureValue = 117, AdventureReward = {rewardid = {870090}}, StorageReward = _EmptyTable, Condition = 64, Desc = '遭遇袭击的爱德华带着不死的魔物来到了一处隐蔽的洞穴，在这里，他找到了一个黑暗心核，借此他脱离了险境，代价是失去了自己的容貌，以及寿命。他是骑士，尽管他身上充斥着黑暗的力量，但是他坚持认为，光明与黑暗只在一念之间。只有邪恶的人，没有邪恶的力量。他决定用这股力量去帮助需要帮助的人，以及…暗中守护着他的爱人。在黑暗魔物大举入侵的最终时刻，黑骑士爱德华用自己所有的力量以及生命，消灭了魔物的头领。他的爱人妮卡最后认出了他的身份，但是逝去的，终究是逝去了…', Level = 0, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53590] = "{id = 53590, NameZh = '恶魔微笑', Icon = 'item_60009', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23533, AdventureValue = 98, AdventureReward = {rewardid = {870091}}, StorageReward = _EmptyTable, Condition = 64, Desc = '一个神奇的宝珠，拥有将一切心智不坚定的人诱惑并且堕落黑暗的魔力，经过恶魔长时间的孕育，威力越来越强大，但是在没有黑暗力量却又心智坚定的人手中却只能被当作珍藏品。', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53591] = "{id = 53591, NameZh = '莫特利之剑', Icon = 'item_40736', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23534, AdventureValue = 98, AdventureReward = {rewardid = {870092}}, StorageReward = _EmptyTable, Condition = 64, Desc = '传说勇者莫特利所持有的武器，跟随莫特利挑战过诸多魔物，根据关于莫特利的记载，莫特利最后一次前往挑战了强大的凯美拉。', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53592] = "{id = 53592, NameZh = '血咒', Icon = 'dark_004', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23535, AdventureValue = 98, AdventureReward = {rewardid = {870093}}, StorageReward = _EmptyTable, Condition = 64, Desc = '如同血色一般，充满诅咒力量的宝石，是德古拉男爵最为喜爱的宝石。通常德古拉男爵在吸完血液后，会滴上一滴人血浸染宝石，日积月累之下，这颗名为血咒的宝石显得更加妖邪。', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53593] = "{id = 53593, NameZh = '天国之门', Icon = 'item_task_12', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23536, AdventureValue = 98, AdventureReward = {rewardid = {870094}}, StorageReward = _EmptyTable, Condition = 64, Desc = '传说看守奥丁神殿之门的兰特克力斯所拥有，奥丁赐予他自由来往神殿与俗世的传送石，但是在诸神的黄昏之后，这天国之门失去了往日的魔力。', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53594] = "{id = 53594, NameZh = '永恒之翼', Icon = 'item_47004', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23537, AdventureValue = 117, AdventureReward = {rewardid = {870095}}, StorageReward = _EmptyTable, Condition = 64, Desc = '传说是由诸神黄昏中战死的女武神背后双翼所化，象征着永恒以及神话，直到今日，上面还存着些许女武神圣洁的魔力。', Level = 80, ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53595] = "{id = 53595, NameZh = '陈旧的手札', Icon = 'item_task_45', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23701, AdventureValue = 85, AdventureReward = {rewardid = {870096}}, StorageReward = _EmptyTable, Condition = 64, Desc = '不知名的古老手札，封面的纸张已经有些泛黄，好像在诉说着它的久远年代。翻开手札，里面有这样一段文字：“亚尔林这个小子最近可真是卖力啊，就连骑士团的奥德里奇队长都对他另眼相看，不过他的脾气确实好得出奇，无论我们怎么捉弄他都不会生气，看来我们以后要对他好一点了。哎，钟楼终于快要建成了，这个时候千万别出什么意外才好”。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53596] = "{id = 53596, NameZh = '破裂的铠甲', Icon = 'item_task_43', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23702, AdventureValue = 98, AdventureReward = {rewardid = {870097}}, StorageReward = _EmptyTable, Condition = 64, Desc = '当邪恶的死灵军团从天而降，奥德里奇队长指挥着骑士们列阵迎敌，激烈的战斗席卷了钟楼里的每个角落，英勇的王国勇士们不断倒在了血泊里，又不断从恶魔们的身上碾压了过去。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53597] = "{id = 53597, NameZh = '亡者的号角', Icon = 'item_task_40', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23703, AdventureValue = 85, AdventureReward = {rewardid = {870098}}, StorageReward = _EmptyTable, Condition = 64, Desc = '一道迅捷的身影在凄美的夜色下，悄悄潜入了黑暗的深渊，深渊里的恶魔争相向他咆哮着，亡者的灵魂视他为腹中的美餐，但他却旁若无人地行走着，口中的言语响彻云表：“亡者的女武神啊，我愿意做你最忠心的仆人，如果你正感到踌躇不决，那么我可以为你指明方向。”', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53598] = "{id = 53598, NameZh = '锈蚀的铁锤', Icon = 'item_task_41', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23704, AdventureValue = 98, AdventureReward = {rewardid = {870099}}, StorageReward = _EmptyTable, Condition = 64, Desc = '当工人们在钟楼的地下发现一块伊米尔之心碎片后，扩散的巨人力量带来了异变的强大魔物，善良的亚尔林为了拯救同伴倒在了魔物的爪牙之下，所幸随行的骑士团卫队及时赶来消灭了魔物，这才避免了更大的伤亡。骑士团取走了伊米尔之心碎片，看样子他们是准备把它进献给王国，可背地里工人们都带着扼腕的语气说这块古怪的碎片将会带来更大的灾难，亚尔林的死亡是白白便宜了其他人。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53599] = "{id = 53599, NameZh = '锋利的匕首', Icon = 'item_task_42', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23705, AdventureValue = 117, AdventureReward = {rewardid = {870100}}, StorageReward = _EmptyTable, Condition = 64, Desc = '嗜血的锋利匕首。当年轻的亚尔林被嘲笑是没用的废物和被遗弃的野种时，他感到了莫大的耻辱，而这耻辱必须以鲜血来洗净。在一个平静的夜晚，他用自己磨制多时的锋利匕首结束了辱人者的生命，从此在大陆上到处流浪，没人知道他的来历，也从没人真正地了解他。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53600] = "{id = 53600, NameZh = '隐匿的面具', Icon = 'item_task_44', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23706, AdventureValue = 117, AdventureReward = {rewardid = {870101}}, StorageReward = _EmptyTable, Condition = 64, Desc = '当所有人都以为亚尔林死于狂暴的魔物之手时，他却神鬼不知地出现在惨烈的战场上，趁着胶着的战斗悄悄取走了伊米尔之心碎片。他渴望成为万众瞩目的强者，而伊米尔之心蕴含的强大能量正是他需要的。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53601] = "{id = 53601, NameZh = '钟楼异闻录·毁灭卷', Icon = 'task_53601', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23707, AdventureValue = 117, AdventureReward = {rewardid = {870102}}, StorageReward = _EmptyTable, Condition = 64, Desc = '亚尔林无疑是一个悲惨的小人物，过去无情的嘲笑和无尽的羞辱使他变得沉默阴狠，他渴望得到强大的力量来改变现状，这种急切的渴望驱使着他做出了一些不可思议的事情。当建筑工人们在钟楼里挖掘出一块伊米尔之心碎片时，古怪的碎片带来了强大的异变魔物，也带给了亚尔林心中无尽的希望。亚尔林精心设计了一个骗局，当工人们都以为他是一个被魔物杀死的悲惨家伙时，他却死而复生地戴上隐匿的面具，行走在黑暗的深渊里。深渊里的恶魔争相向他咆哮着，亡者的灵魂视他为腹中的美餐，但他却旁若无人地行走着，从他口中传出的声音清晰可闻，“亡者的女武神啊，我愿意做你最忠心的仆人，如果你正感到踌躇不决，那么我可以为你指明方向。”他利用了精明的希默梅思，当亡者的大军在黑夜里出征，亚尔林则脱下隐匿的面具安静地等待着即将收获的果实。自私的奥德里奇和贪婪的希默梅思被欲望蒙蔽了双眼，他们只看到眼前的对手，却忽略了隐藏的敌人。潜伏的亚尔林轻易混入了激斗的人群，在混乱中神鬼不知地取走了伊米尔之心碎片，这不仅让愤怒的希默梅思无功而返，也让奥德里奇陷入了无尽的自责之中。这真是一个完美的计划，亚尔林的隐忍和毒辣终于使他如愿以偿。但令人惋惜的是，亚尔林并没有成为最后的赢家。当他面对梦寐以求的神奇碎片时，他的内心并没有感到安稳，反而强大的巨人力量使他慢慢产生异变，这让他备受煎熬。他在内心质问自己，如果变成了一个人人唾弃的魔物，这一切的努力还有什么意义？沉重的压力和心理的落差使他不堪重负，他可以为了强大的力量变得冷漠阴狠，也可以为了强大的力量毁灭一切。当磅礴的力量从指尖划过，这一刻他彻底释放了心中的压抑。狂暴的巨人力量席卷了钟楼的每个角落，也给每个彷徨的生命画上了句点。这是一个悲惨的故事，当那块古怪的碎片被工人们发现时，他们的命运就已经无法改变了。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53602] = "{id = 53602, NameZh = '损坏的怀表', Icon = 'item_task_31', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23804, AdventureValue = 117, AdventureReward = {rewardid = {870106}}, StorageReward = _EmptyTable, Condition = 64, Desc = '耶梦加得使钟楼内的魔物变得更加强大，它们不断逼迫着守护者远离黄金时钟，只有这样耶梦加得才能利用黄金时钟内的伊米尔之心来加速产生时空的异变。此刻这神秘的空间里，激烈碰撞的时空乱流在伊米尔之心的支配下不断塑造着时空的规则，任何陷入这片时空的人都会不由自主地被卷入时间的漩涡，从而永远地从这个世界消失。吉伯特看了看手里已经停止转动的怀表，他终于明白，如果要离开这片平行的空间，那就只能去阻止耶梦加得的阴谋！', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53603] = "{id = 53603, NameZh = '失灵的指针', Icon = 'item_task_36', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23803, AdventureValue = 85, AdventureReward = {rewardid = {870105}}, StorageReward = _EmptyTable, Condition = 64, Desc = '平行的时空里，吉伯特和探险队找遍了钟楼的每个角落，但令人疑惑的是，这里没有嗜血的魔物，也没有扭曲的时空力量，一切都是那么正常，所谓的异变似乎只是一场失控的谣传。吉伯特看了看手中紧握的指针，此刻的他已经有了决定，艾尔帕兰的人们需要知道事情的真相！', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53604] = "{id = 53604, NameZh = '破旧的皮衣', Icon = 'item_42017', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23802, AdventureValue = 85, AdventureReward = {rewardid = {870104}}, StorageReward = _EmptyTable, Condition = 64, Desc = '平行的时空里，吉伯特惊恐地看着一个又一个的队友倒在自己的身旁，他没想到仅仅是因为自己的一个疏忽竟然引起了成群魔物的围攻，此刻的他已经精疲力竭，望着身上皮衣上的斑驳血迹，心中的无力感尤为强烈，而最令他震惊的却是那些从血泊里爬起来的同伴们，此刻已经变成了狰狞的魔物，正争相向他咆哮着……', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53605] = "{id = 53605, NameZh = '恶魔的头盔', Icon = 'item_45210', Type = 63, Quality = 1, MaxNum = 9999, SellPrice = 100, AdventureSort = 23801, AdventureValue = 85, AdventureReward = {rewardid = {870103}}, StorageReward = _EmptyTable, Condition = 64, Desc = '平行的时空里，吉伯特和同伴们顺利地进入了钟楼的深处，凶残的魔物试图阻挡他们的步伐，但一切都是徒劳的。看着钟楼内扭曲的时空和肆虐的能量波动，吉伯特无所畏惧。探险队的行动很快引起了耶梦加得的警觉，苏醒的魔头为了心中的欲望驱使着异空间的魔物向探险队发起了进攻……战斗中的吉伯特和同伴们完美配合，他们斩下了所有恶魔的头颅，完成了探索的任务。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53606] = "{id = 53606, NameZh = '钟楼异闻录·噩梦卷', Icon = 'task_53606', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23805, AdventureValue = 117, AdventureReward = {rewardid = {870107}}, StorageReward = _EmptyTable, Condition = 64, Desc = '在漫长的岁月里，围绕着钟楼的种种诡异现象，曾经有无数勇敢的探险者先后进入钟楼，然而所有的探索都是毫无结果的，因为进入钟楼的探险者从没有人能活着走出来。吉伯特和他的伙伴们与其他的探险者一样，他们怀揣着对艾尔帕兰的热爱和无所畏惧的勇气，踏上了前途未知的发现之旅。他们不知道在前面等待着的会是多么严峻的考验，他们也不知道，此刻的钟楼早已在恶魔耶梦加得的掌控下变得冷酷血腥。恶魔的眼神里没有丝毫怜悯，他利用了伊米尔之心的力量，甚至连大贤者留下的黄金时钟也成为他加速时空异变的工具，而吉伯特他们对此竟然毫不知情。一时间，钟楼内混乱的时空里形成了无数可怕的时间漩涡，任何踏入时间漩涡的人将永远从这个世界上消失。吉伯特和所有的探险者一样，遭遇了裂变时空的吞噬。但他是幸运的，机智灵活的反应使他幸免于难，然而他的伙伴们却永远离开了他，他永远记得那些熟悉的身影通过时空甬道的传送消失在无数个平行世界的尽头。明白一切的吉伯特并没有畏缩地逃跑，他将探险的任务坚持到了最后。当他看着面前有些惊讶的恶魔，这个冷血的杀手，他以自己最无畏的灵魂向恶魔发出了最后一击……', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53607] = "{id = 53607, NameZh = '利维坦的沙漏', Icon = 'item_task_49', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23806, AdventureValue = 98, AdventureReward = {rewardid = {870108}}, StorageReward = _EmptyTable, Condition = 64, Desc = '利维坦是古今著名的大海盗，他利用一个别致的金沙漏来打开时空之门。轻松的穿梭回过去，解决改变未来将出现的麻烦。一天，利维坦的船在海上遇到风暴，嫉妒他的副手乘乱砸破了他的金沙漏，利维坦瞬间化作一缕金沙与沙漏中的沙随风暴飘散在无边无际的大海之中。后来陷害他的副手死于非命，但也没人再见过位大名鼎鼎的海盗。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53608] = "{id = 53608, NameZh = '明日水晶', Icon = 'item_task_46', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23807, AdventureValue = 98, AdventureReward = {rewardid = {870109}}, StorageReward = _EmptyTable, Condition = 64, Desc = '艾尔帕兰城内曾有位通晓未来的魔女，通常是神秘地地罩在面网里，不示人以真面目，脸面向的方向 ，手里永远拿一颗用丝巾包住的水晶球，但是都不展开，仿佛喻世着未来不可知的神秘，没有人知道她叫什么，问她从哪里来，她便答“遥远的未来”', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53609] = "{id = 53609, NameZh = '马莱的指针', Icon = 'item_task_47', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23808, AdventureValue = 98, AdventureReward = {rewardid = {870110}}, StorageReward = _EmptyTable, Condition = 64, Desc = '马莱不仅是位天才魔法师，还是很杰出的钟表师。凭着才华与天赋做出了一只能时间旅行的钟，只要将时针向前推或向后推，便可穿梭回时钟上的时间。可马莱的妻子贪得无厌，想永葆自身青春美貌，折断了马莱钟上的所有指针，于是这位天才魔法师与他的妻子永远的被困在了一个不再有时间流逝的封闭时空内。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53610] = "{id = 53610, NameZh = '时光匕首', Icon = 'item_task_48', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23809, AdventureValue = 117, AdventureReward = {rewardid = {870111}}, StorageReward = _EmptyTable, Condition = 64, Desc = '百年前，艾尔帕兰城外有块能让人忘记时间的石头，歇脚的路人只要一坐在此块石头上便忘记了所有时间和烦恼，不肯再走一步路，城里的一个年轻盗贼请工匠将石头打造成一把匕首，用于行刺。据说用这把匕首靠近敌人，敌人连反抗的意愿都没有，只会乖乖伏降。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53611] = "{id = 53611, NameZh = '时间碎片·年', Icon = 'item_12549', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23708, AdventureValue = 98, AdventureReward = {rewardid = {870112}}, StorageReward = _EmptyTable, Condition = 64, Desc = '研究了黑魔女身体里的魔女印记，诺德温构筑了时间的等级阶层，并研究出时间理论——《永恒维度》，根据研究的结果，他发现时间是一个较为抽象的概念，是物质的演变、运动的表现。于是他写了《时间碎片·年》的研究报告，里面蕴含着时间的部分奥秘。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53612] = "{id = 53612, NameZh = '时间碎片·月', Icon = 'item_12549', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23709, AdventureValue = 98, AdventureReward = {rewardid = {870113}}, StorageReward = _EmptyTable, Condition = 64, Desc = '研究了黑魔女身体里的魔女印记，诺德温构筑了时间的等级阶层，并研究出时间理论——《永恒维度》，根据研究的结果，他发现时间是一切事物构成的保证，并且空间与时间不能分开解析，因此拥有“时空”一说，他写了《时间碎片·月》的研究报告，里面蕴含着时间的部分奥秘。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53613] = "{id = 53613, NameZh = '时间碎片·日', Icon = 'item_12549', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23710, AdventureValue = 98, AdventureReward = {rewardid = {870114}}, StorageReward = _EmptyTable, Condition = 64, Desc = '研究了黑魔女身体里的魔女印记，诺德温构筑了时间的等级阶层，并研究出时间理论——《永恒维度》，根据研究的结果，他发现现在、过去和将来之间的差别只是一种错觉。他写了《时间碎片·日》的研究报告，里面蕴含着时间的部分奥秘。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53614] = "{id = 53614, NameZh = '时间碎片·时', Icon = 'item_12549', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23711, AdventureValue = 98, AdventureReward = {rewardid = {870115}}, StorageReward = _EmptyTable, Condition = 64, Desc = '研究了黑魔女身体里的魔女印记，诺德温构筑了时间的等级阶层，并研究出时间理论——《永恒维度》，根据研究的结果，时间倒流或回到过去，其实是建立在一个不存在的逻辑基础上的。那么又引出一个问题，魔力的存在可以回到过去，那么那时历史又会发生什么变化？他写了《时间碎片·时》的研究报告，里面蕴含着时间的部分奥秘。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53615] = "{id = 53615, NameZh = '时间碎片·分', Icon = 'item_12549', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23712, AdventureValue = 98, AdventureReward = {rewardid = {870116}}, StorageReward = _EmptyTable, Condition = 64, Desc = '研究了黑魔女身体里的魔女印记，诺德温构筑了时间的等级阶层，并研究出时间理论——《永恒维度》，根据研究的结果，魔法的力量可以使人看到过去与未来的事件，在某些超凡的魔法力的影响下，人们可以看到更多，但是，人们无法改变过去既定的事实，那么人类可以通过预言改写未来吗？他写了《时间碎片·分》的研究报告，里面蕴含着时间的部分奥秘。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53616] = "{id = 53616, NameZh = '时间碎片·秒', Icon = 'item_12549', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23713, AdventureValue = 98, AdventureReward = {rewardid = {870117}}, StorageReward = _EmptyTable, Condition = 64, Desc = '研究了黑魔女身体里的魔女印记，诺德温构筑了时间的等级阶层，并研究出时间理论——《永恒维度》，根据研究的结果，时间是一种客观存在。时间的概念是人类认识、归纳、描述自然的结果。魔力的存在促使时间可以在一定程度被人类掌控，但是要想改变历史，改变未来，理论上是可行的，但是实施起来，非常艰难，甚至根本不可能实现。他写了《时间碎片·秒》的研究报告，里面蕴含着时间的部分奥秘。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53617] = "{id = 53617, NameZh = '时间轮盘', Icon = 'item_task_31', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23714, AdventureValue = 117, AdventureReward = {rewardid = {870118}}, StorageReward = _EmptyTable, Condition = 64, Desc = '根据诺德温的描述，他曾经在某个磁场活跃的洞穴里看到了一角未来，那是天崩地裂，山河破碎的末日场景，他看到了伊斯鲁德倒下的剑士雕像，看到了吉芬损坏的吉芬高塔，那角未来消失之后，他得到了这个发着光的轮盘，他至今不知道开启轮盘的方法。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53618] = "{id = 53618, NameZh = '凯撒密码盘', Icon = 'item_task_47', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23810, AdventureValue = 98, AdventureReward = {rewardid = {870119}}, StorageReward = _EmptyTable, Condition = 64, Desc = '这是一面刻满了符文般深奥难解的字符的金色圆盘，盘面上有一些方孔，孔中也有字符，仔细观察会发现它其实是由两面金盘拼接而成，带方孔的金盘可以旋转，使方孔显示不同的字符。若非如此，它容易被误认为是一件魔法物品，然而事实上这是一件用来编译密码的工具。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53619] = "{id = 53619, NameZh = '骷髅吊坠', Icon = 'item_52155', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23811, AdventureValue = 98, AdventureReward = {rewardid = {870120}}, StorageReward = _EmptyTable, Condition = 64, Desc = '这串纤细的金链，连接着一枚黑曜石雕成的骷髅头吊坠，吊坠上透出猫眼般的纹理，似乎有种摄人心魄的能量。拉斯普廷就是借助这枚吊坠来施展他神奇的催眠术。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53620] = "{id = 53620, NameZh = '炼金阵石', Icon = 'item_task_46', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23812, AdventureValue = 98, AdventureReward = {rewardid = {870121}}, StorageReward = _EmptyTable, Condition = 64, Desc = '这是一块残缺的石板，刻满了深奥难解的炼金阵符，这些符文似乎是某个炼金阵的一部分，但现在已经很难根据这部分残缺的符文，推测出整个炼金阵的全貌了。这块石板应该是来自尼古拉的墓室。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53621] = "{id = 53621, NameZh = '蓝色尖帽', Icon = 'item_45067', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23813, AdventureValue = 98, AdventureReward = {rewardid = {870122}}, StorageReward = _EmptyTable, Condition = 64, Desc = '这是一顶深蓝色的尖帽，上面印着许多黄色的明星，象征着魔法师来自星空的深邃魔力，它是灰袍巫师身份的象征之一。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53622] = "{id = 53622, NameZh = '黄金镰刀', Icon = 'item_41815', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23814, AdventureValue = 98, AdventureReward = {rewardid = {870123}}, StorageReward = _EmptyTable, Condition = 64, Desc = '这把黄金锻造的镰刀是大德鲁伊用来采集神圣的槲寄生的圣物，阿莱斯特˙克劳利曾经极度仰慕德鲁伊，他为了制作黄金镰刀，甚至无视禁令肆意掠夺，对他来说，德鲁伊就是他的信仰。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53623] = "{id = 53623, NameZh = '星界棱晶', Icon = 'item_52821', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23815, AdventureValue = 117, AdventureReward = {rewardid = {870124}}, StorageReward = _EmptyTable, Condition = 64, Desc = '来自神秘时空乱流的特殊晶体，通体紫色光泽，四周还包裹着一阵氤氲的淡紫色雾气，纳鲁认为他是来自神秘星空，是星空的馈赠，其中蕴含不知名的力量，似乎与那些异世界来客身上的力量一样……', ItemTarget = _EmptyTable, NoSale = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53624] = "{id = 53624, NameZh = '溯回指针', Icon = 'item_task_37', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23715, AdventureValue = 98, AdventureReward = {rewardid = {870125}}, StorageReward = _EmptyTable, Condition = 64, Desc = '任何事物都无法抗拒吞食一切的时间，这天伯纳德、尼克西、雷萨吉、海廷斯、黑鲁携手闯入钟楼，却遭遇了时间支配者，他们应该如何选择？', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53625] = "{id = 53625, NameZh = '破败钟表·六点', Icon = 'item_task_38', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23716, AdventureValue = 98, AdventureReward = {rewardid = {870126}}, StorageReward = _EmptyTable, Condition = 64, Desc = '贪婪而好占便宜的人，容易被收买。尼克西就是这样的人。尼克西带着三个木箱踏上了逃亡之路，三个沉重的木箱是他活命的掣肘。但是他不愿意放手，他总是心怀侥幸，那个梦魇会追上其他人，伯纳德也好，海廷斯也好。只要不先来找自己，自己就可以凭借对钟楼的熟悉率先逃出去，然后享用这数不尽的财富。魔鬼追上来，魔鬼的魔法使得三个木箱越来越重，最后尼克西无法负担木箱的重负，被生生压死。魔鬼放下一个破败的钟表，此刻，钟表的时间指向六点。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53626] = "{id = 53626, NameZh = '破败钟表·三点', Icon = 'item_task_38', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23717, AdventureValue = 98, AdventureReward = {rewardid = {870127}}, StorageReward = _EmptyTable, Condition = 64, Desc = '傲慢而刚愎自负的人，容易被利用，雷萨吉就是这样的人。如果他肯与伯纳德一同上路，如果他肯向魔鬼求饶道歉，如果他愿意与其他死人联手对抗时间支配者。也许结局不会变成受尽折磨而死。他的傲慢与自负导致了他的死亡。魔鬼放下了一个破败的钟表，此刻，钟表的时间指向三点。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53627] = "{id = 53627, NameZh = '破败钟表·九点', Icon = 'item_task_38', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23718, AdventureValue = 98, AdventureReward = {rewardid = {870128}}, StorageReward = _EmptyTable, Condition = 64, Desc = '暴躁而崇尚暴力的人，容易被拖垮，海廷斯就是这样的人。暴躁的人永远都是这样，自以为暴力可以解决一切。但是实际上，暴力只会产生暴力，滥用暴力的人终究会倒在更强的暴力之下。魔鬼召唤出近乎无尽的魔物，海廷斯虽然力量不凡，但终究还是倒在了魔物手中。魔鬼放下了一个破败的钟表，此刻，钟表的时间指向九点。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53628] = "{id = 53628, NameZh = '破败钟表·十二点', Icon = 'item_task_38', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23719, AdventureValue = 98, AdventureReward = {rewardid = {870129}}, StorageReward = _EmptyTable, Condition = 64, Desc = '奸诈而做事刁钻的人，容易被舍弃，伯纳德就是这样的。为了增强自己的力量来到钟楼，在时钟支配者的干预下，只能迎来自己的死亡。魔鬼亲手用魔法的力量把这位追寻力量的魔法师送入地狱。看着魔法师被魔法冰封的遗体，魔鬼放下了一个破败的钟表，此刻，钟表的时间指向十二点。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53629] = "{id = 53629, NameZh = '死亡丧钟', Icon = 'item_task_39', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23720, AdventureValue = 117, AdventureReward = {rewardid = {870130}}, StorageReward = _EmptyTable, Condition = 64, Desc = '懦弱而依赖他人的人，容易被欺骗，黑鲁就是这样的人。尽管在时间支配者的帮忙下，黑鲁完成了自己的复仇。但骨子里，黑鲁依然是个懦弱的人，走到时间支配者面前也好，报仇也好，都不是依靠自己的力量。在死亡丧钟的面前，他没有选择的余地。只能按照丧钟的时间，给予他们死亡。钟声响起，人魂尽丧，这就是死亡丧钟。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53630] = "{id = 53630, NameZh = '钟楼异闻录·丧钟卷', Icon = 'task_53630', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23721, AdventureValue = 117, AdventureReward = {rewardid = {870131}}, StorageReward = _EmptyTable, Condition = 64, Desc = '尼克西为人贪婪而好占便宜，因此他被伯纳德所收买。雷萨吉为人傲慢且刚愎自负，因此他被伯纳德所利用。海廷斯暴躁且崇尚暴力，因此他最终被无尽魔物拖死。伯纳德奸诈且做事刁钻，不留余地，因此遭到其余三人在事情发生后无情地舍弃。至于黑鲁，他从未改变过，懦弱且依赖他人，因此最终他的灵魂被魔鬼吞噬。黑鲁死去，遗留的死亡丧钟给予人们警示，傲慢、贪婪、暴躁、奸诈、懦弱，这些人性的弱点，将在时间的流逝之下逐渐暴露。而到了那时，死亡丧钟将会再一次敲响。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53631] = "{id = 53631, NameZh = '德斯黎的指环', Icon = 'item_44003', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23816, AdventureValue = 98, AdventureReward = {rewardid = {870132}}, StorageReward = _EmptyTable, Condition = 64, Desc = '时间的力量变幻莫测，时间黑洞却告知了过往。他们三人的至交好友斯科特突然在这钟楼二层失踪。而没过几天，钟楼就传出了关于鬼魅的传说，阿奇索伦认为，那个鬼魅就是斯科特。但是蒙妮卡和德斯黎却不置可否，但是耐不住阿奇索伦的软磨硬泡，三人还是决定前往钟楼一探究竟，结果他们却遭遇了魔物的袭击…', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53632] = "{id = 53632, NameZh = '时间魔石·过往', Icon = 'item_12536', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23817, AdventureValue = 98, AdventureReward = {rewardid = {870133}}, StorageReward = _EmptyTable, Condition = 64, Desc = '斯科特与德斯黎显然是一对好朋友。在黑洞出现的那刻，他想抓住斯科特，时间黑洞吞噬着周围的一切，在这场拉锯战中，人最终是失败者，斯科特狠狠的推开德斯黎，自己却因为反作用力被黑洞吞噬。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53633] = "{id = 53633, NameZh = '时间魔石·未来', Icon = 'item_12536', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23818, AdventureValue = 98, AdventureReward = {rewardid = {870134}}, StorageReward = _EmptyTable, Condition = 64, Desc = '时间，捉摸不透，人们无法看到未来究竟发生了什么。阿奇索伦坚持认为德斯黎与斯科特的逝去与神秘的鬼影有关。蒙妮卡劝他不要再去那个诡异的地方了，但是阿奇索伦却毫不在意。经过许久的等待，他终于等到了那个鬼影，一番激烈的战斗后阿奇索伦摘下了鬼影的面具，却发现鬼影竟然是蒙妮卡！', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53634] = "{id = 53634, NameZh = '时间魔石·现在', Icon = 'item_12536', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23819, AdventureValue = 117, AdventureReward = {rewardid = {870135}}, StorageReward = _EmptyTable, Condition = 64, Desc = '时间是世界上一切成就的土壤。时间给空想者痛苦，给创造者幸福。在斯科特于黑洞中消失之后，他决定扮成鬼影，驱逐那些误入这里的人们。当德斯黎为了救阿奇索伦深陷黑暗，他临死前的最后一句话，让蒙妮卡接替了他的工作。于是，每晚十二点过后的，总有一双阴森的双眼注视着钟楼二层。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53635] = "{id = 53635, NameZh = '钟楼异闻录·魅影卷', Icon = 'task_53635', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23820, AdventureValue = 117, AdventureReward = {rewardid = {870136}}, StorageReward = _EmptyTable, Condition = 64, Desc = '时间是世界上一切成就的土壤。时间给空想者痛苦，给创造者幸福。德斯黎便是创造者，他在创造一个通向安全的方向牌。他决定扮成鬼影，驱逐那些误入这里的人们。同时他又是勇敢的，他为了救阿奇索伦永陷黑暗，即使在这个时候，他也没有忘记让蒙妮卡接替了他的工作。在这世界上并不缺少创造者，区别在于，一个创造出真正的幸福，而另一个只是幻想罢了。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53636] = "{id = 53636, NameZh = '褪色的木灵', Icon = 'item_12547', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23601, AdventureValue = 75, AdventureReward = {rewardid = {870137}}, StorageReward = _EmptyTable, Condition = 64, Desc = '森灵一族守护的三种灵石之一，代表生机与自然的力量，但是因为人类肆意研究破坏环境，环境的污染影响了秘境中的木灵，在污染物的侵蚀下，木灵变得不再纯净，在它的影响下，树林、植被以及以这些为生的生物都发生了巨大的变化。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53637] = "{id = 53637, NameZh = '褪色的水灵', Icon = 'item_12545', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23602, AdventureValue = 75, AdventureReward = {rewardid = {870138}}, StorageReward = _EmptyTable, Condition = 64, Desc = '森灵一族守护的三种灵石之一，代表生命与滋养的力量，但是因为人类肆意研究破坏环境，环境的污染影响了秘境中的水灵，在污染物的侵蚀下，水灵变得不再纯净，在它的影响下，山川、湖泊以及以这些为生的生物都发生了巨大的变化。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53638] = "{id = 53638, NameZh = '褪色的土灵', Icon = 'item_12546', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23603, AdventureValue = 75, AdventureReward = {rewardid = {870139}}, StorageReward = _EmptyTable, Condition = 64, Desc = '森灵一族守护的三种灵石之一，代表厚重与生长的力量，但是因为人类肆意研究破坏环境，环境的污染影响了秘境中的土灵，在污染物的侵蚀下，土灵变得不再纯净，在它的影响下，土地、生长在土地上的植被以及以这些为生的生物都发生了巨大的变化。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53639] = "{id = 53639, NameZh = '森灵之心', Icon = 'item_60003', Type = 63, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureSort = 23604, AdventureValue = 90, AdventureReward = {rewardid = {870140}}, StorageReward = _EmptyTable, Condition = 64, Desc = '森灵一族的至宝，木、水、土三灵石被伊米尔之心的力量所污染，三灵石滋养下的森灵之心即将受到污染的时候，森灵王果断切断了森灵之心与灵石的联系，虽然森灵之心没有遭受污染，但是失去了三灵石的供养，森灵之心逐渐失去了力量，现在森灵王正在寻找让森灵之心重新恢复活力的方法。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53640] = "{id = 53640, NameZh = '破碎的刀刃', Icon = 'item_40016', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23901, AdventureValue = 98, AdventureReward = {rewardid = {870141}}, StorageReward = _EmptyTable, Condition = 64, Desc = '经过吉芬法师们的调查，女神之手的阴谋渐渐被揭露出来，希默梅斯聚集了一批邪恶的魔物在克雷斯特汉姆，试图献祭法师庞德的灵魂，召唤出黑暗之王毁灭整个大陆。为了阻止他们的阴谋，全大陆的各势力：高傲的法师、轻盈的弓箭手、英武的皇家骑士团，团结起来组成了古城同盟军。这支全大陆最精锐的部队集结在古城之中，与克雷斯特汉姆嗜血的魔物决战，至死方休。同盟军在进入古城时碰见了一件棘手的事情，这片区域被阴影和浓雾笼罩，许多魔物蛰伏在浓雾之中，随时准备攻击误入他们领地的人类。女法师莉莎使用冒险者从古城中找到的破碎的刀刃，建立了通向古城下水道的传送阵，帮助同盟军进入古城下水道寻找庞德的踪影。这些同盟军战士在下水道误入大魔物迷幻之王的陷阱，迷幻之王吞噬了庞德部分的灵魂，躲藏在古城下水道想要继续吞噬更多的灵魂，危急时刻英雄法师莉莎使用魔法把众人传送回安全区域。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53641] = "{id = 53641, NameZh = '暗黑手札', Icon = 'item_task_45', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23902, AdventureValue = 98, AdventureReward = {rewardid = {870142}}, StorageReward = _EmptyTable, Condition = 64, Desc = '同盟军的先锋部队在古城调查时发现了一对误入暗黑手札的恋人，他们被封印在这本暗神官的手札中无法解脱。先锋部队立刻对他们开展了解救行动，被解救的二人为同盟军带来古城最深处的信息，他们在手札里挣扎时得以窥视到一群绝望祭祀在古城墓地的深处举行献祭仪式。法师莉莎立刻利用暗黑手札，帮助同盟军建立了通向古城墓地的传送阵。同盟军的先锋部队前往古城墓地调查，在阴森的墓地里艰难的前行，终于找到了正在举行献祭仪式的绝望祭祀。先锋部队合力击破绝望祭祀的仪式，阻止他们继续献祭，但也惊动了迷幻之王。发现仪式被破坏的迷幻之王大发雷霆，要毁灭先锋部队，幸好大法师雷藏及时赶到挡住迷幻之王的攻击。莉莎也顺利完成构建传送阵的任务，大家得以撤退，保留实力，为最终的决战做准备。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53642] = "{id = 53642, NameZh = '黑暗锁链', Icon = 'item_5039', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 23903, AdventureValue = 98, AdventureReward = {rewardid = {870143}}, StorageReward = _EmptyTable, Condition = 64, Desc = '虽然阻止了绝望祭祀的献祭，但庞德的灵魂依然在黑暗之王的掌控之中，黑暗之王利用庞德的弱点，把他的灵魂分裂成几部分。大法师摩根调查到庞德灵魂在他不稳定的精神世界里，先锋部队成员冒着生命危险进入了庞德的精神世界。了解到这位普通的法师，在学习过程中碰见的挫折和绝望，正是这股绝望，让黑暗之王有机可趁。先锋部队的成员在庞德的精神世界不停给庞德鼓舞，帮助这位并不出众的法师一部分灵魂脱离了黑暗之王的掌控。成功夺回庞德部分的灵魂之后，先锋部队一鼓作气进入迷幻之王的死亡空间，在这个乌烟瘴气的空间解救出庞德的另外一部分灵魂，胜利在望！死亡空间的深处是黑暗之王的所在地，他企图占据庞德的身体，以古城为据点率领他的魔物军团进攻整个人类世界。尽管黑暗之王并没有被完全召唤，但是他凌驾在人类之上的实力，让同盟军对他无计可施。正当人类的明天将会走向灰暗之时，女武神的英灵们及时赶到，配合人类的精英部队一起对抗黑暗之王，把这个魔头赶回了深渊。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53643] = "{id = 53643, NameZh = '灵魂静谧', Icon = 'item_44002', Type = 41, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '法师协会专门打造的项链灵魂静谧。\n使用后HP上限永久 + 15', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53644] = "{id = 53644, NameZh = '默斯曼的感谢信', Icon = 'item_5032', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 24001, AdventureValue = 98, AdventureReward = {rewardid = {870145}}, StorageReward = _EmptyTable, Condition = 64, Desc = '默斯曼为了感谢冒险者的帮助赠送给冒险者的谢礼。冒险者们受邀参加艾尔帕兰一场盛大的婚礼，婚礼的现场新郎意外遇害，冒险者义不容辞调查事件真相。与此同时，记录黄金时钟使用方法的“秘法典籍”失窃。冒险者追踪窃贼之时，却被时钟守护者霍兰干扰，冒险者对霍兰心起疑问。通过跟踪霍兰，发现他确实是内贼，霍兰不仅帮助窃贼盗取“秘法典籍”，还准备煽动守护者内部爆发叛乱，夺取默斯曼的性命。冒险者透过霍兰继续调查，得知原来指挥霍兰的幕后黑手是三瘟神之一的耶梦加德，他试图利用黄金时钟的力量，解救自己被锁在月亮上的哥哥魔狼芬里尔，如果让他的阴谋得逞，不仅是艾尔帕兰，整个大陆都会陷入危险之中。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53645] = "{id = 53645, NameZh = '诺曼的怀表', Icon = 'item_task_31', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 24002, AdventureValue = 98, AdventureReward = {rewardid = {870146}}, StorageReward = _EmptyTable, Condition = 64, Desc = '时钟守护者诺曼的怀表。耶梦加德为了获得黄金时钟的力量，伪装成守护者诺曼的弟弟。他在艾尔帕兰经营多年，了解到许多有关黄金时钟的秘密，他指使霍兰煽动时钟守护者们，利用他们爆发内部叛乱，导致默斯曼受了重伤，趁着默斯曼受伤的时机，盗取奇点球。耶梦加德在钟楼里利用奇点球，召唤出古代巴风特，他妄想着古代巴风特会帮助他杀死时钟守护者的领袖默斯曼·岁月。没想到巴风特觊觎他的神躯，不听从他的命令，反而倒戈相向，妄图吞噬耶梦加德。危急时刻，诺曼舍命相救，诺曼的行为唤醒他内心的良善，耶梦加德与冒险者合作击败了古代巴风特。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53646] = "{id = 53646, NameZh = '黄金蛇眼', Icon = 'item_7337', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 24003, AdventureValue = 98, AdventureReward = {rewardid = {870147}}, StorageReward = _EmptyTable, Condition = 64, Desc = '轮回梦魇的眼球。因为耶梦加德过度使用奇点球和黄金时钟的力量，导致黄金时钟内部产生了时空裂隙。另外一条时间线的耶梦加德穿越黑洞而来，而那个世界的耶梦加德没有选择使用黄金时钟拯救哥哥，反而选择自己吞噬了这股的力量，也因为这股力量心智全失，变成了一个嗜血的怪物，他给自己起名为轮回梦魇，流窜于不同世界。耶梦加德联合冒险者，打败另一个世界的自己——轮回梦魇，帮助人类封印了黑洞，并承诺不再使用黄金时钟就悄然离去。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53647] = "{id = 53647, NameZh = '时钟守卫者勋章', Icon = 'item_5261', Type = 41, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '默斯曼为英勇的冒险者颁发的时钟守卫者勋章。\n使用后HP上限永久 + 15', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53648] = "{id = 53648, NameZh = '红鼻子入场券', Icon = 'item_12317', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 25001, AdventureValue = 98, AdventureReward = {rewardid = {870149}}, StorageReward = _EmptyTable, Condition = 64, Desc = '今年的圣诞节，姜饼城来了一个神秘小丑，他特别喜欢糖果和孩子，免费为姜饼城的小孩子发放马戏团的入场券——巧克力饼干。持有这种巧克力饼干的小孩可以观看红鼻子马戏团的表演，令人担忧的是很多看完表演的小孩都失踪了。圣诞老人委托前来姜饼城游玩的冒险者调查小丑，接受委托的冒险者，伪装成小孩子的模样混入马戏团里，陪伴一位名叫珍妮的女孩观看红鼻子马戏团的表演，前两场表演都顺利的演完，冒险者也找到了一些调查线索。据线索可知，一位叫做毕尤的傀儡巫师指示小丑把姜饼城的孩子们制作成玩偶，圣诞老人非常气愤，姜饼城的所有居民都团结起来一起对抗毕尤和小丑。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53649] = "{id = 53649, NameZh = '圣诞节快乐', Icon = 'item_12121', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 25002, AdventureValue = 98, AdventureReward = {rewardid = {870150}}, StorageReward = _EmptyTable, Condition = 64, Desc = '名字叫做圣诞节快乐的糖果，糖果商人贝蒂的祖传配方，许多年来一直被评选为最受孩子欢迎的糖果，只在圣诞节限量供应。贝蒂小时候，每年的圣诞节爸爸妈妈会带她到白雪纷飞的姜饼城度假，在这里她认识了一个可爱的小丑加尔，她把爸爸熬制的糖果“圣诞节快乐”送给了加尔，与加尔成为了好朋友。贝蒂与加尔约定，每年圣诞节都会在姜饼城相聚，贝蒂将学会熬制糖果，亲手为加尔制作出”圣诞节快乐“，而加尔也会好好锻炼自己的演出技艺专门为贝蒂表演。然而，一些意外发生，让贝蒂没能实现与加尔的约定，加尔也因为贝蒂的失约变成了疯狂的魔物。几十年后，贝蒂和加尔重新在圣诞村相遇，一颗”圣诞节快乐“解开了他们的心结。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53650] = "{id = 53650, NameZh = '圣诞树的铃铛', Icon = 'item_52312', Type = 41, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '帮助圣诞老人赶走傀儡巫师毕尤后，圣诞老人给予的奖励，象征了姜饼城的小铃铛。\n使用后HP上限永久 + 15', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53651] = "{id = 53651, NameZh = '波利留声器', Icon = 'item_12342', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 26001, AdventureValue = 98, AdventureReward = {rewardid = {870152}}, StorageReward = _EmptyTable, Condition = 64, Desc = '炼金大师飞利浦的发明的波利形状的留声器，里面记载了他给学生阿克米的留言：亲爱的阿克米，进来无恙？老师碰见了一件麻烦的事情需要你帮助，希望你可以尽快来一趟姜饼城，越快越好。对了，这是我的新发明——波利留声器，可以录音和播放，你觉得有趣吗？', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53652] = "{id = 53652, NameZh = '阿克米的礼品盒', Icon = 'item_3664', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 26002, AdventureValue = 98, AdventureReward = {rewardid = {870153}}, StorageReward = _EmptyTable, Condition = 64, Desc = '阿克米制作的礼品盒，打开时会播放清脆的音乐。虽然心中有些怨恨自己收养的两个小孩，阿克米还是按照老师的吩咐为他们制作了玩具和盛放玩具的礼品盒。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53653] = "{id = 53653, NameZh = '阿克米的饼干人', Icon = 'item_45408', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 26003, AdventureValue = 98, AdventureReward = {rewardid = {870154}}, StorageReward = _EmptyTable, Condition = 64, Desc = '阿克米制作的饼干人，看上去非常像真正的饼干，上面还有许多小孩子的牙印。阿克米为自己的养子养女制作的玩具，被小孩们当成了真正的饼干。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53654] = "{id = 53654, NameZh = '阿克米的玩具士兵', Icon = 'Cruiser', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 26004, AdventureValue = 98, AdventureReward = {rewardid = {870155}}, StorageReward = _EmptyTable, Condition = 64, Desc = '阿克米制作的玩具士兵，看上去神气又威武。炼金术士阿克米为养子养女制作的玩具，被保存的非常完美。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53655] = "{id = 53655, NameZh = '茉莉香水', Icon = 'item_12341', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 26005, AdventureValue = 98, AdventureReward = {rewardid = {870156}}, StorageReward = _EmptyTable, Condition = 64, Desc = '使用东方神秘植物茉莉精炼而成的香水。炼金术士阿克米为自己的养女小幸运制作的结婚礼物，饱含了他对女儿的爱意。连瓶身都是小巧的留声器，按下按钮会播放阿克米对女儿的祝福。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53656] = "{id = 53656, NameZh = '地龙皮手套', Icon = 'item_44023', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 26006, AdventureValue = 98, AdventureReward = {rewardid = {870157}}, StorageReward = _EmptyTable, Condition = 64, Desc = '使用稀有的地龙皮制作的手套，最适合刚入行不熟练的炼金术士。有效的保护炼金术士的双手不被强酸和强碱的腐蚀，阿克米买个自己的养子小勇气的礼物。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53657] = "{id = 53657, NameZh = '炼金术士的日记本', Icon = 'item_3605', Type = 165, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53658] = "{id = 53658, NameZh = '没有装饰的铜镜', Icon = 'item_52316', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 27001, AdventureValue = 98, AdventureReward = {rewardid = {870159}}, StorageReward = _EmptyTable, Condition = 64, Desc = '奥莉薇亚有着一双灵气十足的大眼睛，麦斯是她的青梅竹马，一天麦斯带奥莉薇亚出城看星星，却遇到了雷雨天气，马车和人一同翻进了冰冷的湖水，夺走了麦斯年轻的生命，而奥莉薇亚却失明了。当她在医院醒来时，牧师告诉她运气很好，刚好有适合的眼睛能取代她失明的双目，在恢复视力的那天，牧师给奥莉薇亚拿来了一盏没有装饰的铜镜，望向镜中的自己时，奥莉维亚的眼泪却止不住的流了下来。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53659] = "{id = 53659, NameZh = '羊皮箭筒', Icon = 'item_47020', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 27002, AdventureValue = 98, AdventureReward = {rewardid = {870160}}, StorageReward = _EmptyTable, Condition = 64, Desc = '蕾拉和她年仅六岁的妹妹正在参加婶婶的葬礼，在葬礼上，蕾拉对一个从未谋面的英俊的弓箭手一见钟情。蕾拉天生性格内向，并没有上前搭讪说话。年轻男子在葬礼上落下了一个羊皮箭筒，蕾拉看见后悄悄的收了起来。葬礼结束后，蕾拉借着箭筒的契机和城中的人打听这位年轻的弓箭手，可是怎么打听也打听不到男子的下落。一个月后，蕾拉的妹妹去世了，在妹妹的葬礼上她终于见到了那个让她魂牵梦萦的弓箭手。当蕾拉将箭筒还给男子时，蕾拉说：“抱歉，我很小心的收着，但还是弄丢了一只。”', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53660] = "{id = 53660, NameZh = '冰晶玫瑰', Icon = 'item_52509', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 27003, AdventureValue = 98, AdventureReward = {rewardid = {870161}}, StorageReward = _EmptyTable, Condition = 64, Desc = '妮可儿在一户富裕人家长大，妈妈却独宠哥哥十分排挤她，不仅虐待她，还让她为家中做许多苦力。倒是村口的一个穷苦妇人对她格外亲切，时常偷偷塞些小樱桃给她吃。一天，妇人交给妮可儿一朵冰晶玫瑰，说这朵玫瑰里住着雪精灵能满足人的一个愿望，妇人让妮可儿用这朵花许愿永远幸福。妮可儿因为回家晚了，被妈妈一顿毒打，泪水滴在了玫瑰上，一个雪精灵真的从花蕊中飞了出来，妮可儿忘记了妇人的叮嘱，仇恨已充斥了她的内心，她说她希望她的家人全死光。第二天清晨，妮可儿醒了，发现爸爸，妈妈，哥哥都在，妮可儿愣了楞，突然大哭起来，跑去问雪精灵可不可以收回愿望，可雪精灵说一切都太晚了。昨日妇人给的樱桃散落了一地，染红了白雪。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53661] = "{id = 53661, NameZh = '灵偶发带', Icon = 'item_45261', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 27004, AdventureValue = 98, AdventureReward = {rewardid = {870162}}, StorageReward = _EmptyTable, Condition = 64, Desc = '这三个被诅咒的娃娃诞生于一个怪异的玩具工人之手，这位工人在制作完这些娃娃的第三天就离奇的去世了。一个魔女在每个娃娃的头发上都绑上了一根淡蓝色的发带。从此之后，人们就时常会在玩具工厂看到这三只到处游荡的娃娃。她们靠夺取少女的心灵，来获得自由活动的能力，所以常找些精神状态不稳定的少女下手。悄悄潜入少女们的房间，将蓝色的灵偶发带解下系在她们的头上。便搭建了，进入少女心灵的桥梁。鸢尾喜欢少女的绝望，所以选中了奥莉薇亚，她的恋人牺牲了自己将双眼给了失明的奥莉薇亚，让奥莉薇亚悲伤欲绝；紫罗兰爱少女的恐惧，蕾拉为了再次在葬礼上见到心仪的男子不惜亲手杀死自己的妹妹，回到家后的蕾拉并没注意到她床上多了一只漂亮精致的人偶；洛丽玛丝看到妮可儿因为仇恨蒙蔽了她的双眼，因此误杀了自己的亲生母亲而感到痛苦不堪。而这种痛苦，正是洛丽玛丝所需要的。三个命运多舛的少女就这样被诅咒的娃娃戴上了灵偶发带，成了她们的傀儡。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53662] = "{id = 53662, NameZh = '金线兔玩偶', Icon = 'item_12343', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 26007, AdventureValue = 98, AdventureReward = {rewardid = {870163}}, StorageReward = _EmptyTable, Condition = 64, Desc = '唐纳的母亲是个穷苦的纺织工，随着圣诞节即将到来，唐纳要求母亲给自己买一个兔子玩偶。橱窗中的兔子玩偶十分昂贵，母亲并没有那么多钱，就告诉唐纳，她会给唐纳手工做一个同样可爱的玩偶。可唐纳就是想要商店里的那只，哭闹不止。母亲没有办法，母亲只好去偷隔壁商贩家的东西卖，不巧被商贩发现了。商贩没有将钱要回来，而是剁下了母亲一只手臂。平安夜的那天晚上，唐纳如愿以偿的得到了兔子玩偶，可他不知道母亲再也不能纺织了。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53663] = "{id = 53663, NameZh = '苦涩的糖豆', Icon = 'item_12121', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 26008, AdventureValue = 98, AdventureReward = {rewardid = {870164}}, StorageReward = _EmptyTable, Condition = 64, Desc = '格兰迪是领主家的小童工，领主很喜欢这个机灵的小家伙。每次格兰迪做完工，领主都会奖励格兰迪一把甜甜的糖豆。可格兰迪觉得做工太辛苦了，他宁可坐着都能得到奖励，于是他就派自己的影子去帮他做工。他的影子和格兰迪一模一样，连领主都没看出分别来。日复一日，影子渐渐取代了格兰迪的位置。那天，影子在交给领主给格兰迪的奖励中，偷偷的混入了一颗抹着毒药的糖豆。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53664] = "{id = 53664, NameZh = '旋转红鞋', Icon = 'item_43523', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 26009, AdventureValue = 98, AdventureReward = {rewardid = {870165}}, StorageReward = _EmptyTable, Condition = 64, Desc = '一个叫卡伦的农村女孩在她母亲死后被一个富有的老妇人收养了。从前，卡伦只有破旧的木鞋，可现在养母给她买了一双高级定制的红舞鞋。卡伦经常不顾礼数穿着红舞鞋去严肃的教堂，甚至，当她的养母生病时，她还穿着红舞鞋去参加派对。一天，一位教堂门口的老太太对她说了些奇怪的话后，卡伦便脱不下她的舞鞋，舞鞋自己疯狂的跳动，日日夜夜，晴雨不分，永不停歇。卡伦只好找到了一个刽子手，祈求他砍掉自己的脚。刽子手砍下了卡伦的脚，但是舞鞋依然在跳动，带着卡伦的脚消失在浓浓的夜色中。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53665] = "{id = 53665, NameZh = '恶魔金沙', Icon = 'item_52404', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 26010, AdventureValue = 98, AdventureReward = {rewardid = {870166}}, StorageReward = _EmptyTable, Condition = 64, Desc = '马克的爷爷卧床不起，病情严重，他绝望地唤出了恶魔，说若能使爷爷病愈，他便献给恶魔一千克金沙。恶魔用法术使马克的爷爷康复了。爷爷病好后，马克没用真正的金沙来酬谢恶魔，而是用黄沙代替，放在祭坛上烧，并念念有词的说：“恶魔啊恶魔，我已经履行了我的承诺。”恶魔便在晚上托梦告诉小马克，要他到海边去，说在那里可以找到真正的金沙。马克醒来后，高兴极了，直往海边跑去。结果在那里遇到了凶恶的海盗，马克被他们抓走并且卖掉，而他的售卖价格是…一千克金沙。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53666] = "{id = 53666, NameZh = '说话的鱼', Icon = 'item_550511', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 26011, AdventureValue = 98, AdventureReward = {rewardid = {870167}}, StorageReward = _EmptyTable, Condition = 64, Desc = '托尼跟着哥哥出海打渔，织了一张和桌子一般大的小网。出海一天只捕到了一条鱼，垂头丧气地回到了家。回家将鱼放进水中，鱼居然开口说了话。鱼对小托尼说：\"把网织得越大，捕到的鱼就越多。\" 托尼听了鱼的话，就认真地在家织网。哥哥劝托尼不要那么贪心，但托尼不理会。巨网织好后，小托尼就带着它和哥哥出海，花了好大的功夫才把巨网撒入大海。托尼想，这一定可以装满一船，想着想着，他乐得笑出了声。 但当托尼准备收网了，一拉网，觉得好沉好沉，连船带人一起翻入了深海之中。哥哥一人回到家，发现家中那条会说话的鱼也不见了。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53667] = "{id = 53667, NameZh = '冰冻的心脏', Icon = 'item_52203', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 26012, AdventureValue = 98, AdventureReward = {rewardid = {870168}}, StorageReward = _EmptyTable, Condition = 64, Desc = '南希是富有人家的孩子，他有一个属于自己的小花园。圣诞时，南希的父母给南希布置了一颗完美的圣诞树。别家的孩子都想来这里看一看南希的圣诞树，可南希太吝啬了，他不愿放任何人进自己的小花园。于是，当春天笼罩大地，唯独南希的花园还是冬天的天气，温暖的大地无法融化南希冷冰冰的心。南希抱着光秃秃毫无装饰的圣诞树，永远的睡在了雪里。多年后，人们从树底发现了一颗被冰冻的心脏。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53668] = "{id = 53668, NameZh = '小鬼四叶草', Icon = 'item_52302', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 26013, AdventureValue = 98, AdventureReward = {rewardid = {870169}}, StorageReward = _EmptyTable, Condition = 64, Desc = '艾玛是公爵的女儿，傲慢且盛气凌人。人们都爱围绕着她，因为公爵宠溺小艾玛。所有好吃的好玩的都属于她一人。艾玛想要一片四叶草，但这在市面上可买不到。于是公爵便命人将附近的草丛全部铲除，也要找到那片四叶草。住在草里的精灵，告诉艾玛这一带没有四叶草，并祈求她不要毁了他们的住所。艾玛不为所动，还是铲平了附近的所有草丛，但这惊扰了住在草丛中的小鬼。小鬼问艾玛是不是要四叶草，艾玛点头。于是小鬼取走了艾玛的舌头，双眼和头发，并给了艾玛一片四叶草。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53669] = "{id = 53669, NameZh = '托尼的眼球', Icon = 'dark_001', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 26014, AdventureValue = 98, AdventureReward = {rewardid = {870170}}, StorageReward = _EmptyTable, Condition = 64, Desc = '托尼和托塔是一对孪生兄弟。但哥哥托尼总是嫉妒父母对弟弟托塔的照顾。一天托尼遇到一只金鸟，金鸟告诉他，可以满足他任何一个愿望，但前提是他的弟弟托塔会得到双倍的报酬。托尼起先高兴不已。但他仔细一想：如果他想要一座糖果屋，弟弟就会有两座。如果他想要一车玩具，弟弟就会有两车。想来想去，托尼不知道该提什么要求才好，他实在不甘心又被弟弟白占了便宜。最后，托尼一咬牙，对金鸟说：“哎，你挖去我的一只眼珠吧。”', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53670] = "{id = 53670, NameZh = '坎卜斯的桦树枝', Icon = 'item_12021', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 26015, AdventureValue = 98, AdventureReward = {rewardid = {870171}}, StorageReward = _EmptyTable, Condition = 64, Desc = '坎卜斯是个半羊半魔的怪物，常在平安夜现身，他将考察圣诞村的每一个孩子，并在坏孩子的门口放上桦树枝。这天晚上，孩子们会胆战心惊的查看，摆在门外的鞋子或靴子中装的是圣诞老公公给的馈赠，还是坎卜斯给坏孩子的树枝。被放上树枝的人家，代表着被他盯上的坏孩子，会被坎卜斯拿桦树枝抽打，再把他们带回自己的巢穴。在巢穴中等着他们的，永远是坎卜斯为坏孩子们准备的那份特殊的惩罚礼物。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53671] = "{id = 53671, NameZh = '陈旧的告示', Icon = 'item_5128', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 27005, AdventureValue = 98, AdventureReward = {rewardid = {870172}}, StorageReward = _EmptyTable, Condition = 64, Desc = '从布袋熊的布袋中滑出一封陈旧的告示，上面登记着十年前的一天，圣诞村的宝石商人贝尔摩被洗劫的新闻。贝尔摩身中数箭身亡，当时的抢匪一共有五个人，查理、曼逊、丹、里克和卡尔。因四人指认是腹语师查理杀了贝尔摩，所以腹语师查理被判绞刑，在平安夜的前一天被处决。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53672] = "{id = 53672, NameZh = '波利玩偶', Icon = 'item_12342', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 27006, AdventureValue = 98, AdventureReward = {rewardid = {870173}}, StorageReward = _EmptyTable, Condition = 64, Desc = '打开发条熊背后的暗门，里面藏着一只波利玩偶。这个玩偶属于死去的贝尔摩先生的小女儿。这个可怜的小女孩，那天正好是她的生日，当时受到了过多的惊吓，只知道重复着一句话：“他们中的首领把我和爸爸绑了起来，杀死我爸爸的人和卡尔多拿了两颗钻石”。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53673] = "{id = 53673, NameZh = '丹的密信', Icon = 'item_task_34', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 27007, AdventureValue = 98, AdventureReward = {rewardid = {870174}}, StorageReward = _EmptyTable, Condition = 64, Desc = '撕开毛绒熊的身体，从里面露出来一封被封住的信。信上写着：“首领，我与你的妻子还有卡尔可是手足关系，切记不可相信里克这个人，虽然他常拿好处巴结你，但毕竟不是一家人。我看得出你也不是很相信他，等风波过去了，我就和您碰头。丹。”', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53674] = "{id = 53674, NameZh = '小女儿的画', Icon = 'item_555022', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 27008, AdventureValue = 98, AdventureReward = {rewardid = {870175}}, StorageReward = _EmptyTable, Condition = 64, Desc = '剥去纽扣熊的纽扣，从熊身体里探出来的是一张贝尔摩小女儿的画。上面有一行模糊的字：首领大人，我给您寄来了贝尔摩小女儿的画。我理解您现在一定不好受，因为您曾是这小姑娘的美术老师。我也理解您为了袒护真正凶手不得已的作为，毕竟那是您最要好的兄弟。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53675] = "{id = 53675, NameZh = '生锈的箭', Icon = 'item_12502', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 27009, AdventureValue = 98, AdventureReward = {rewardid = {870176}}, StorageReward = _EmptyTable, Condition = 64, Desc = '划开皮革熊的皮革，能从里面取出一只折断的箭。箭上虽然已经锈迹斑斑，但依旧能看出它当年的威力应该不小。这正是十年前杀死贝尔摩先生的箭。从贝尔摩身上拔下好几根这样的断箭，可以看出，这都是由同一把弓射出去的。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53676] = "{id = 53676, NameZh = '有钻石的肩包', Icon = 'item_47031', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 27010, AdventureValue = 98, AdventureReward = {rewardid = {870177}}, StorageReward = _EmptyTable, Condition = 64, Desc = '割开牵线熊的绳子，从牵线熊的肚中露出一只咖啡色的小包。这只包乍一看平淡无奇，却重的可疑。实际上里面有四个装着钻石的小布袋，每个小布袋上都缝着一个人名。首领的布袋和里克的布袋中只有一颗钻石，其余两人的布袋中则装着两颗钻石。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53677] = "{id = 53677, NameZh = '腹语师陶板', Icon = 'item_12528', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 27011, AdventureValue = 98, AdventureReward = {rewardid = {870178}}, StorageReward = _EmptyTable, Condition = 64, Desc = '腹语师在被处死前，用指甲刻字在陶板上，陈述了十年前富商惨案的真相。十年前腹语师查理被钻石商贝尔摩请到家中，帮他的小女儿庆祝生日，为其表演腹语节目。曼逊是抢匪四人的首领，带着三人来家中抢夺贝尔摩的宝石。丹射箭杀死了贝尔摩先生，因为卡尔出力多，所以两人多分到了一颗钻石。里克很慌张，并不想承担任何责任，所以四人指正查理为真正的凶手。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53678] = "{id = 53678, NameZh = '圣诞金铃', Icon = 'item_52312', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 27012, AdventureValue = 98, AdventureReward = {rewardid = {870179}}, StorageReward = _EmptyTable, Condition = 64, Desc = '姜饼城的小孩胖熊从圣诞树附近捡到的精致铃铛，摇晃时会发出清脆悦耳的铃声，如果跳舞时佩戴在身上会随着舞步叮叮当当作响。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53679] = "{id = 53679, NameZh = '蝴蝶磷光水晶', Icon = 'item_900008', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 27013, AdventureValue = 98, AdventureReward = {rewardid = {870180}}, StorageReward = _EmptyTable, Condition = 64, Desc = '混合了暗夜蝴蝶磷粉的水晶，在黑夜里熠熠发光。美丽的舞娘请热心的冒险者，在午夜时刻捕捉到圣诞村特有的暗夜蝴蝶，制作出带着蝴蝶磷光的水晶。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53680] = "{id = 53680, NameZh = '金苹果', Icon = 'item_157', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 27014, AdventureValue = 98, AdventureReward = {rewardid = {870181}}, StorageReward = _EmptyTable, Condition = 64, Desc = '漂亮的金苹果，黑夜之中会发出金灿灿的光芒，是黑天鹅舞蹈团的舞娘们亲手制作的舞蹈道具。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
	[53681] = "{id = 53681, NameZh = '迷之树叶', Icon = 'item_task_54', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 27015, AdventureValue = 98, AdventureReward = {rewardid = {870182}}, StorageReward = _EmptyTable, Condition = 64, Desc = '画有各种神秘箭头和符号，隐隐约约之中可以发现好像是一个什么奇怪地形的地图，上面还标有一些宝藏样子的符号。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53682] = "{id = 53682, NameZh = '圣诞香包', Icon = 'item_3708', Type = 165, Quality = 3, MaxNum = 9999, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '黑天鹅舞蹈团的舞娘们为了游客制作的圣诞纪念品，存放了各种珍贵香料，拿在手上可以闻到迷人的香味。舞娘珂莲安为了感谢冒险者的帮助，将自己亲手制作的圣诞香包赠送给热心的冒险者。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53683] = "{id = 53683, NameZh = '吉尔达的金发', Icon = 'item_task_53', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 27016, AdventureValue = 98, AdventureReward = {rewardid = {870184}}, StorageReward = _EmptyTable, Condition = 64, Desc = '吉尔达女神的一缕头发，太阳神弗雷曾偷偷坐在奥丁的宝座上观察远方，他看见北国有一位美丽的女神，长了一头秀丽的金发，从此就爱上了那位女神。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53684] = "{id = 53684, NameZh = '聚金戒指', Icon = 'item_44017', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 27017, AdventureValue = 98, AdventureReward = {rewardid = {870185}}, StorageReward = _EmptyTable, Condition = 64, Desc = '太阳神弗雷的定情信物，爱上吉尔达的太阳神请求史基尔尼的帮助，撮合他跟吉尔达女神。史基尔尼答应了太阳神的请求，带着他的定情信物聚金戒指来到了北国。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[53685] = "{id = 53685, NameZh = '永恒之花', Icon = 'item_task_16', Type = 63, Quality = 2, MaxNum = 9999, SellPrice = 100, AdventureSort = 27018, AdventureValue = 98, AdventureReward = {rewardid = {870186}}, StorageReward = _EmptyTable, Condition = 64, Desc = '精灵之国亚尔夫海姆的花朵，被太阳神制作成了不会凋谢的瓶中花，送给吉尔达女神，作为第一次见面的礼物。', ItemTarget = _EmptyTable, NoSale = 1, NoStorage = 1, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
