	[145283] = "{id = 145283, NameZh = '金色的进行曲[1]', Icon = 'item_45283', Type = 840, Quality = 1, MaxNum = 1, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '时值初秋。贝尔佛第宫的仆人们正在忙着里外奔波，这里即将举行盛大的茶会。届时将有世界各地的贵妇人们身穿雍容华贵的服装佩戴着各种璀璨夺目宝石前来参加这场盛宴，美学与美学的直接碰撞，在皇家仪仗队的鼓点声中，这场“演出”才刚开始。', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[145284] = "{id = 145284, NameZh = '致·爱丽丝[1]', Icon = 'item_45284', Type = 800, Quality = 1, MaxNum = 1, SellPrice = 100, AdventureSort = 9510, AdventureValue = 1, AdventureReward = _EmptyTable, StorageReward = {buffid = {80012320}}, Condition = 8, Desc = '少年在整理曾祖母遗物的时候发现了一个尘封已久的小小古典木箱，时隔几个世纪后这部即将影响后人作品再次沐浴在阳光下。世间人们早已无人知晓真正的故事，但是少年手上的那把钥匙依然刻着曾经的那段美丽的心愿。', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
	[145285] = "{id = 145285, NameZh = '夜后的咏叹调[1]', Icon = 'item_45285', Type = 830, Quality = 1, MaxNum = 1, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '“亨切利~亨切利~哦，这小家伙又上哪里去了，让他好好整理乐谱书籍的事居然到现在都还没办！真是气死我了！”“亨切利，我和你说了多少次了，那个倒霉笨蛋音乐家的事你就别去管他了，见鬼~你有没有在听！这套叫《魔笛》的乐谱根本就没有销路，不要再练习了，回来搬书，我还有很多东西要你做！比如…”', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[145286] = "{id = 145286, NameZh = '月·光[1]', Icon = 'item_45286', Type = 800, Quality = 1, MaxNum = 1, SellPrice = 100, AdventureSort = 9512, AdventureValue = 1, AdventureReward = _EmptyTable, StorageReward = {buffid = {80012340}}, Condition = 8, Desc = '【月光为桨，莲花作舟；皮埃罗向着南方顺风启程了。流水哼着深沉的曲调，摇晃着单薄的小船。月光为桨，莲花作舟。回贝加莫去，回故乡去，皮埃罗踏上了归途；看东方苍苍的地平线泛起了蒙蒙曙光。……月光为桨。】', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
	[145287] = "{id = 145287, NameZh = '小恶魔耳机[1]', Icon = 'item_45287', Type = 800, Quality = 1, MaxNum = 1, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[145288] = "{id = 145288, NameZh = '南瓜发带[1]', Icon = 'item_45288', Type = 800, Quality = 1, MaxNum = 1, SellPrice = 100, AdventureReward = _EmptyTable, StorageReward = _EmptyTable, Desc = '', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, TFValidDate = '', ValidDate = ''}",
	[145291] = "{id = 145291, NameZh = '勇者都怕我[1]', Icon = 'item_45291', Type = 800, Quality = 1, MaxNum = 1, SellPrice = 100, AdventureSort = 9516, AdventureValue = 1, AdventureReward = _EmptyTable, StorageReward = {buffid = {80012320}}, Condition = 8, Desc = '经历了无数的辗转和颠簸，无数次的领土扩张，魔王终于拥有了自己的国度，以他的美学所定义的“自由”国度，即使没有了夜之少女的歌唱，依然有诗篇记录着这段历史-孑然一身独自君临着这个幻想国度的魔王。', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
	[145293] = "{id = 145293, NameZh = '艾希头饰[1]', Icon = 'item_45293', Type = 800, Quality = 1, MaxNum = 1, AuctionPrice = 1, SellPrice = 100, AdventureSort = 10019, AdventureValue = 90, AdventureReward = _EmptyTable, StorageReward = {buffid = {80012350}}, Condition = 8, Desc = '艾希在无尽的轮回中，获得了真正的自我。时间空间流转，她，成为了旅人。', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
