	[145297] = "{id = 145297, NameZh = '白色圣诞[1]', Icon = 'item_45297', Type = 800, Quality = 3, MaxNum = 1, SellPrice = 100, AdventureSort = 9521, AdventureValue = 1, AdventureReward = _EmptyTable, StorageReward = {buffid = {80012500}}, Condition = 8, Desc = '“祖母躺在吱呀吱呀如同钟摆节奏般的老旧木质躺椅上，一针一线纺织着鲜艳色彩的袜子，父亲挂在壁炉上的冬青花环在噼里啪啦的火光照耀显得更加鲜艳。母亲所正在忙碌的厨房里飘出了各种孩子们根本无法忍受的诱惑香味。呼口气就能变成水晶的玻璃外，银色正在悄然地包裹着城市，映照出红彤彤的可爱脸颊和清澈如阿瓦隆湖水一样的眼瞳。”“啊~我们在此许愿~”', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
	[145298] = "{id = 145298, NameZh = '加油小鹿[1]', Icon = 'item_45298', Type = 800, Quality = 2, MaxNum = 1, SellPrice = 100, AdventureSort = 9522, AdventureValue = 1, AdventureReward = _EmptyTable, StorageReward = {buffid = {80012510}}, Condition = 8, Desc = '“打开天窗等待着他，床边放着袜子等待着他，虚掩着门扉等待着他，兴奋又想偷看结果还是睡着的夜晚~啦啦啦~啦啦啦啦~红鼻子的鲁道夫今晚就要飞过我的窗口，今年他也会停留吧？啦啦啦~啦啦啦~红鼻子的鲁道夫驮着那个人，在长大前好想见他一面。”', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
