	[145434] = "{id = 145434, NameZh = '普隆德拉的守护[1]', Icon = 'item_45434', Type = 800, Quality = 3, MaxNum = 1, SellPrice = 100, AdventureSort = 9527, AdventureValue = 1, AdventureReward = _EmptyTable, StorageReward = {buffid = {80012760}}, Condition = 8, Desc = '“我真的很讨厌这里！”凯莉赌气地说道，“这里人声又嘈杂，每天到哪里都有穿着各种奇装异服的家伙假装要好心帮忙来勾搭！”，“…莫名其妙又把这个月探险经费都去购买了一堆没有用的帽子！每天都勾引我！每天都勾引我！”，“哼！我真的讨厌这里。”凯莉说着说着，生气的表情里流着眼泪开始笑了，她转过头去，此时一阵微风吹拂过首都广场中央的景观喷泉，将少女美丽的黑发悠然地抛向天空。嗯~这孩子，还真是喜欢这里呢！', ItemTarget = _EmptyTable, MediaPath = '', GetLimit = _EmptyTable, JobLimit = _EmptyTable, Feature = 1, TFValidDate = '', ValidDate = ''}",
